","keccak256":"0x9431fd772ed4abc038cdfe9ce6c0066897bd1685ad45848748d1952935d5b8ef"},"contracts/Utils/Tokenlock.sol":{"content":"pragma solidity ^0.5.16;

import "./Owned.sol";

contract Tokenlock is Owned {
    /// @notice Indicates if token is locked
    uint8 internal isLocked = 0;

    event Freezed();
    event UnFreezed();

    modifier validLock() {
        require(isLocked == 0, "Token is locked");
        _;
    }

    function freeze() public onlyOwner {
        isLocked = 1;

        emit Freezed();
    }

    function unfreeze() public onlyOwner {
        isLocked = 0;

        emit UnFreezed();
    }
}
