// SPDX-License-Identifier: BSD-3-Clause
pragma solidity 0.8.25;

import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";

interface VTokenInterface {
    function _reduceReserves(uint reduceAmount) external returns (uint);

    function _acceptAdmin() external returns (uint);

    function comptroller() external returns (address);

    function _setInterestRateModel(address newInterestRateModel) external returns (uint);
}

interface IWBNB is IERC20Upgradeable {
    function deposit() external payable;
}

interface IProtocolShareReserve {
    enum IncomeType {
        SPREAD,
        LIQUIDATION
    }

    function updateAssetsState(address comptroller, address asset, IncomeType incomeType) external;
}

contract VBNBAdminStorage {
    /// @notice address of protocol share reserve contract
    IProtocolShareReserve public protocolShareReserve;

    /// @dev gap to prevent collision in inheritence
    uint256[49] private __gap;
}

contracts/DelegateBorrowers/MoveDebtDele.content:
// SPDX-License-Identifier: BSD-3-Clause
pragma solidity 0.8.25;

import { Ownable2StepUpgradeable } from "@openzeppelin/contracts-upgradeable/access/Ownable2StepUpgradeable.sol";
import { ReentrancyGuardUpgradeable } from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import { SafeERC20Upgradeable } from "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import { IERC20Upgradeable } from "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import { ResilientOracleInterface } from "@venusprotocol/oracle/contracts/interfaces/OracleInterface.sol";
import { ensureNonzeroAddress } from "@venusprotocol/solidity-utilities/contracts/validators.sol";

import { approveOrRevert } from "../lib/approveOrRevert.sol";
import { IVBep20, IComptroller } from "../InterfacesV8.sol";

contract MoveDebtDelegate is Ownable2StepUpgradeable, ReentrancyGuardUpgradeable {
    /// @dev VToken return value signalling about successful execution
    uint256 internal constant NO_ERROR = 0;

    /// @notice A wildcard indicating that repayment is allowed for _any_ user in the market
    address public constant ANY_USER = address(1);

    /// @notice User to borrow on behalf of
    /// @custom:oz-upgrades-unsafe-allow state-variable-immutable
    address public immutable newBorrower;

    /// @notice Whether to allow borrowing from the corresponding vToken
    mapping(address => bool) public borrowAllowed;

    /// @notice Whether to allow repaying to the corresponding vToken on behalf of
    ///   a certain user. Use ANY_USER to check if repayment is allowed for any user.
    mapping(address => mapping(address => bool)) public repaymentAllowed;

    /// @notice Emitted when vToken is allowed or denied to be borrowed from
    event BorrowAllowedSet(address indexed vTokenToBorrow, bool allowed);

    /// @notice Emitted when vToken is allowed or denied to be borrowed from
    event RepaymentAllowedSet(address indexed vTokenToRepay, address indexed originalBorrower, bool allowed);

    /// @notice Emitted if debt is swapped successfully
    event DebtMoved(
        address indexed originalBorrower,
        address indexed vTokenRepaid,
        uint256 repaidAmount,
        address newBorrower,
        address indexed vTokenBorrowed,
        uint256 borrowedAmount
    );

    /// @notice Emitted when the owner transfers tokens, accidentially sent to this contract,
    ///   to their account
    event SweptTokens(address indexed token, uint256 amount);

    /// @notice Thrown if VTokens' comptrollers are not equal
    error ComptrollerMismatch();

    /// @notice Thrown if repayment fails with an error code
    error RepaymentFailed(uint256 errorCode);

    /// @notice Thrown if borrow fails with an error code
    error BorrowFailed(uint256 errorCode);

    /// @notice Thrown if borrowing from the corresponding vToken is not allowed
    error BorrowNotAllowed(address vToken);

    /// @notice Thrown if repaying the debts of the borrower to the corresponding vToken is not allowed
    error RepaymentNotAllowed(address vToken, address borrower);

    using SafeERC20Upgradeable for IERC20Upgradeable;

    /// @notice Constructor for the implementation contract. Sets immutable variables.
    /// @param newBorrower_ User to borrow on behalf of
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor(address newBorrower_) {
        newBorrower = newBorrower_;
        _disableInitializers();
    }

    function initialize() external initializer {
        __Ownable2Step_init();
        __ReentrancyGuard_init();
    }

    /**
     * @notice Repays originalBorrower's borrow in vTokenToRepay.underlying() and borrows
     *   vTokenToBorrow.underlying() on behalf of newBorrower.
     *
     * @param originalBorrower The address of the borrower, whose debt to repay
     * @param vTokenToRepay VToken to repay to on behalf of originalBorrower
     * @param repayAmount The amount to repay in terms of vTokenToRepay.underlying()
     * @param vTokenToBorrow VToken to borrow from
     */
    function moveDebt(
        IVBep20 vTokenToRepay,
        address originalBorrower,
        uint256 repayAmount,
        IVBep20 vTokenToBorrow
    ) external nonReentrant {
        if (!borrowAllowed[address(vTokenToBorrow)]) {
            revert BorrowNotAllowed(address(vTokenToBorrow));
        }

        mapping(address => bool) storage repaymentAllowedFor = repaymentAllowed[address(vTokenToRepay)];
        if (!repaymentAllowedFor[ANY_USER] && !repaymentAllowedFor[originalBorrower]) {
            revert RepaymentNotAllowed(address(vTokenToRepay), originalBorrower);
        }

        uint256 actualRepaymentAmount = _repay(vTokenToRepay, originalBorrower, repayAmount);
        uint256 amountToBorrow = _convert(vTokenToRepay, vTokenToBorrow, actualRepaymentAmount);
        _borrow(vTokenToBorrow, amountToBorrow);
        emit DebtMoved(
            originalBorrower,
            address(vTokenToRepay),
            actualRepaymentAmount,
            newBorrower,
            address(vTokenToBorrow),
            amountToBorrow
        );
    }

    /**
     * @notice Allows or denies borrowing from the corresponding vToken
     * @param vTokenToBorrow VToken to borrow from
     * @param allow Whether to allow borrowing from the corresponding vToken
     */
    function setBorrowAllowed(address vTokenToBorrow, bool allow) external onlyOwner {
        ensureNonzeroAddress(vTokenToBorrow);
        if (borrowAllowed[vTokenToBorrow] != allow) {
            borrowAllowed[vTokenToBorrow] = allow;
            emit BorrowAllowedSet(vTokenToBorrow, allow);
        }
    }

    /**
     * @notice Allows or denies repaying the debts of originalBorrower to the corresponding vToken
     * @param vTokenToRepay VToken to repay to
     * @param originalBorrower The address of the borrower, whose debt to repay (or ANY_USER to allow
     *   repayments for all users in the market, e.g. if the market is going to be deprecated soon)
     * @param allow Whether to allow repaying to the corresponding vToken on behalf of originalBorrower
     */
    function setRepaymentAllowed(address vTokenToRepay, address originalBorrower, bool allow) external onlyOwner {
        ensureNonzeroAddress(vTokenToRepay);
        ensureNonzeroAddress(originalBorrower);
        if (repaymentAllowed[vTokenToRepay][originalBorrower] != allow) {
            repaymentAllowed[vTokenToRepay][originalBorrower] = allow;
            emit RepaymentAllowedSet(vTokenToRepay, originalBorrower, allow);
        }
    }

    /**
     * @notice Transfers tokens, accidentially sent to this contract, to the owner
     * @param token ERC-20 token to sweep
     */
    function sweepTokens(IERC20Upgradeable token) external onlyOwner {
        uint256 amount = token.balanceOf(address(this));
        token.safeTransfer(owner(), amount);
        emit SweptTokens(address(token), amount);
    }

    /**
     * @dev Transfers the funds from the sender and repays a borrow in vToken on behalf of the borrower
     * @param vTokenToRepay VToken to repay to
     * @param borrower The address of the borrower, whose debt to repay
     * @param repayAmount The amount to repay in terms of underlying
     */
    function _repay(
        IVBep20 vTokenToRepay,
        address borrower,
        uint256 repayAmount
    ) internal returns (uint256 actualRepaymentAmount) {
        IERC20Upgradeable underlying = IERC20Upgradeable(vTokenToRepay.underlying());
        uint256 balanceBefore = underlying.balanceOf(address(this));
        underlying.safeTransferFrom(msg.sender, address(this), repayAmount);
        uint256 balanceAfter = underlying.balanceOf(address(this));
        uint256 repayAmountMinusFee = balanceAfter - balanceBefore;

        uint256 borrowBalanceBefore = vTokenToRepay.borrowBalanceCurrent(borrower);
        approveOrRevert(underlying, address(vTokenToRepay), repayAmountMinusFee);
        uint256 err = vTokenToRepay.repayBorrowBehalf(borrower, repayAmountMinusFee);
        if (err != NO_ERROR) {
            revert RepaymentFailed(err);
        }
        approveOrRevert(underlying, address(vTokenToRepay), 0);
        uint256 borrowBalanceAfter = vTokenToRepay.borrowBalanceCurrent(borrower);
        return borrowBalanceBefore - borrowBalanceAfter;
    }

    /**
     * @dev Borrows in vToken on behalf of the borrower and transfers the funds to the sender
     * @param vTokenToBorrow VToken to borrow from
     * @param borrowAmount The amount to borrow in terms of underlying
     */
    function _borrow(IVBep20 vTokenToBorrow, uint256 borrowAmount) internal {
        IERC20Upgradeable underlying = IERC20Upgradeable(vTokenToBorrow.underlying());
        uint256 balanceBefore = underlying.balanceOf(address(this));
        uint256 err = vTokenToBorrow.borrowBehalf(newBorrower, borrowAmount);
        if (err != NO_ERROR) {
            revert BorrowFailed(err);
        }
        uint256 balanceAfter = underlying.balanceOf(address(this));
        uint256 actualBorrowedAmount = balanceAfter - balanceBefore;
        underlying.safeTransfer(msg.sender, actualBorrowedAmount);
    }

    /**
     * @dev Converts the value expressed in convertFrom.underlying() to a value
     *   in convertTo.underlying(), using the oracle price
     * @param convertFrom VToken to convert from
     * @param convertTo VToken to convert to
     * @param amount The amount in convertFrom.underlying()
     */
    function _convert(IVBep20 convertFrom, IVBep20 convertTo, uint256 amount) internal view returns (uint256) {
        IComptroller comptroller = convertFrom.comptroller();
        if (comptroller != convertTo.comptroller()) {
            revert ComptrollerMismatch();
        }
        ResilientOracleInterface oracle = comptroller.oracle();

        // Decimals are accounted for in the oracle contract
        uint256 scaledUsdValue = oracle.getUnderlyingPrice(address(convertFrom)) * amount; // the USD value here has 36 decimals
        return scaledUsdValue / oracle.getUnderlyingPrice(address(convertTo));
    }
}

contracts/DelegateBorrowers/SwapDebtDele.content:
// SPDX-License-Identifier: BSD-3-Clause
pragma solidity 0.8.25;

import { Ownable2StepUpgradeable } from "@openzeppelin/contracts-upgradeable/access/Ownable2StepUpgradeable.sol";
import { ReentrancyGuardUpgradeable } from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import { SafeERC20Upgradeable } from "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import { IERC20Upgradeable } from "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import { ResilientOracleInterface } from "@venusprotocol/oracle/contracts/interfaces/OracleInterface.sol";

import { approveOrRevert } from "../lib/approveOrRevert.sol";
import { IVBep20, IComptroller } from "../InterfacesV8.sol";

contract SwapDebtDelegate is Ownable2StepUpgradeable, ReentrancyGuardUpgradeable {
    /// @dev VToken return value signalling about successful execution
    uint256 internal constant NO_ERROR = 0;

    /// @notice Emitted if debt is swapped successfully
    event DebtSwapped(
        address indexed borrower,
        address indexed vTokenRepaid,
        uint256 repaidAmount,
        address indexed vTokenBorrowed,
        uint256 borrowedAmount
    );

    /// @notice Emitted when the owner transfers tokens, accidentially sent to this contract,
    ///   to their account
    event SweptTokens(address indexed token, uint256 amount);

    /// @notice Thrown if VTokens' comptrollers are not equal
    error ComptrollerMismatch();

    /// @notice Thrown if repayment fails with an error code
    error RepaymentFailed(uint256 errorCode);

    /// @notice Thrown if borrow fails with an error code
    error BorrowFailed(uint256 errorCode);

    using SafeERC20Upgradeable for IERC20Upgradeable;

    function initialize() external initializer {
        __Ownable2Step_init();
        __ReentrancyGuard_init();
    }

    /**
     * @notice Repays a borrow in repayTo.underlying() and borrows borrowFrom.underlying()
     * @param borrower The address of the borrower, whose debt to swap
     * @param repayTo VToken to repay the debt to
     * @param borrowFrom VToken to borrow from
     * @param repayAmount The amount to repay in terms of repayTo.underlying()
     */
    function swapDebt(
        address borrower,
        IVBep20 repayTo,
        IVBep20 borrowFrom,
        uint256 repayAmount
    ) external onlyOwner nonReentrant {
        uint256 actualRepaymentAmount = _repay(repayTo, borrower, repayAmount);
        uint256 amountToBorrow = _convert(repayTo, borrowFrom, actualRepaymentAmount);
        _borrow(borrowFrom, borrower, amountToBorrow);
        emit DebtSwapped(borrower, address(repayTo), actualRepaymentAmount, address(borrowFrom), amountToBorrow);
    }

    /**
     * @notice Transfers tokens, accidentially sent to this contract, to the owner
     * @param token ERC-20 token to sweep
     */
    function sweepTokens(IERC20Upgradeable token) external onlyOwner {
        uint256 amount = token.balanceOf(address(this));
        token.safeTransfer(owner(), amount);
        emit SweptTokens(address(token), amount);
    }

    /**
     * @dev Transfers the funds from the sender and repays a borrow in vToken on behalf of the borrower
     * @param vToken VToken to repay the debt to
     * @param borrower The address of the borrower, whose debt to repay
     * @param repayAmount The amount to repay in terms of underlying
     */
    function _repay(
        IVBep20 vToken,
        address borrower,
        uint256 repayAmount
    ) internal returns (uint256 actualRepaymentAmount) {
        IERC20Upgradeable underlying = IERC20Upgradeable(vToken.underlying());
        uint256 balanceBefore = underlying.balanceOf(address(this));
        underlying.safeTransferFrom(msg.sender, address(this), repayAmount);
        uint256 balanceAfter = underlying.balanceOf(address(this));
        uint256 repayAmountMinusFee = balanceAfter - balanceBefore;

        underlying.safeApprove(address(vToken), 0);
        underlying.safeApprove(address(vToken), repayAmountMinusFee);
        uint256 borrowBalanceBefore = vToken.borrowBalanceCurrent(borrower);
        uint256 err = vToken.repayBorrowBehalf(borrower, repayAmountMinusFee);
        if (err != NO_ERROR) {
            revert RepaymentFailed(err);
        }
        uint256 borrowBalanceAfter = vToken.borrowBalanceCurrent(borrower);
        return borrowBalanceBefore - borrowBalanceAfter;
    }

    /**
     * @dev Borrows in vToken on behalf of the borrower and transfers the funds to the sender
     * @param vToken VToken to borrow from
     * @param borrower The address of the borrower, who will own the borrow
     * @param borrowAmount The amount to borrow in terms of underlying
     */
    function _borrow(IVBep20 vToken, address borrower, uint256 borrowAmount) internal {
        IERC20Upgradeable underlying = IERC20Upgradeable(vToken.underlying());
        uint256 balanceBefore = underlying.balanceOf(address(this));
        uint256 err = vToken.borrowBehalf(borrower, borrowAmount);
        if (err != NO_ERROR) {
            revert BorrowFailed(err);
        }
        uint256 balanceAfter = underlying.balanceOf(address(this));
        uint256 actualBorrowedAmount = balanceAfter - balanceBefore;
        underlying.safeTransfer(msg.sender, actualBorrowedAmount);
    }

    /**
     * @dev Converts the value expressed in convertFrom.underlying() to a value
     *   in convertTo.underlying(), using the oracle price
     * @param convertFrom VToken to convert from
     * @param convertTo VToken to convert to
     * @param amount The amount in convertFrom.underlying()
     */
    function _convert(IVBep20 convertFrom, IVBep20 convertTo, uint256 amount) internal view returns (uint256) {
        IComptroller comptroller = convertFrom.comptroller();
        if (comptroller != convertTo.comptroller()) {
            revert ComptrollerMismatch();
        }
        ResilientOracleInterface oracle = comptroller.oracle();

        // Decimals are accounted for in the oracle contract
        uint256 scaledUsdValue = oracle.getUnderlyingPrice(address(convertFrom)) * amount; // the USD value here has 36 decimals
        return scaledUsdValue / oracle.getUnderlyingPrice(address(convertTo));
    }
}

contracts/Governance/TokenRedeemer.sol.content:
// SPDX-License-Identifier: BSD-3-Clause
pragma solidity 0.8.25;

import { ReentrancyGuard } from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import { Ownable2Step } from "@openzeppelin/contracts/access/Ownable2Step.sol";
import { ensureNonzeroAddress } from "@venusprotocol/solidity-utilities/contracts/validators.sol";

import { IVAIController, IVToken, IVBep20, IVBNB } from "../InterfacesV8.sol";
import { Currency, CurrencyLibrary } from "../lib/Currency.sol";

contract TokenRedeemer is ReentrancyGuard, Ownable2Step {
    using CurrencyLibrary for Currency;

    struct Repayment {
        address borrower;
        uint256 amount;
    }

    IVBNB public immutable VBNB;

    error AccrueInterestFailed(uint256 errCode);
    error RedeemFailed(uint256 errCode);
    error RepaymentFailed(uint256 errCode);
    error NativeTokenTransferFailed();

    constructor(address owner_, IVBNB vBNB) {
        ensureNonzeroAddress(owner_);
        VBNB = vBNB;
        _transferOwnership(owner_);
    }

    receive() external payable {}

    function redeemAndTransfer(IVToken vToken, address destination) external nonReentrant onlyOwner {
        Currency underlying = _underlying(vToken);
        uint256 err = vToken.redeem(vToken.balanceOf(address(this)));
        if (err != 0) {
            revert RedeemFailed(err);
        }
        underlying.transferAll(destination);
    }

    function redeemUnderlyingAndTransfer(
        IVToken vToken,
        address destination,
        uint256 amount,
        address receiver
    ) external nonReentrant onlyOwner {
        Currency underlying = _underlying(vToken);
        underlying.transferAll(receiver); // Just in case there were some underlying tokens on the contract
        uint256 err = vToken.redeemUnderlying(amount);
        if (err != 0) {
            revert RedeemFailed(err);
        }
        underlying.transferAll(destination);
        Currency.wrap(address(vToken)).transferAll(receiver);
    }

    function redeemUnderlyingAndRepayBorrowBehalf(
        IVToken vToken,
        address borrower,
        uint256 amount,
        address receiver
    ) external nonReentrant onlyOwner {
        Currency underlying = _underlying(vToken);

        uint256 err = vToken.redeemUnderlying(amount);
        if (err != 0) {
            revert RedeemFailed(err);
        }

        underlying.approve(address(vToken), amount);

        _repay(vToken, borrower, amount);

        underlying.approve(address(vToken), 0);

        underlying.transferAll(receiver);
        Currency.wrap(address(vToken)).transferAll(receiver);
    }

    function redeemAndBatchRepay(
        IVToken vToken,
        Repayment[] calldata requestedRepayments,
        address receiver
    ) external nonReentrant onlyOwner {
        _accrueInterest(vToken);

        (uint256 totalBorrowedAmount, Repayment[] memory repayments) = _getAmountsToRepay(vToken, requestedRepayments);
        _redeemUpTo(vToken, totalBorrowedAmount);

        Currency underlying = _underlying(vToken);
        uint256 balance = underlying.balanceOfSelf();
        underlying.approve(address(vToken), totalBorrowedAmount);
        uint256 repaymentsCount = repayments.length;
        // The code below assumes no fees on transfer
        if (balance >= totalBorrowedAmount) {
            // If we're doing a full repayment, we can optimize it by skipping the balance checks
            for (uint256 i = 0; i < repaymentsCount; ++i) {
                Repayment memory repayment = repayments[i];
                _repay(vToken, repayment.borrower, repayment.amount);
            }
        } else {
            // Otherwise, we have to check and update the balance on every iteration
            for (uint256 i = 0; i < repaymentsCount && balance != 0; ++i) {
                Repayment memory repayment = repayments[i];
                _repay(vToken, repayment.borrower, _min(repayment.amount, balance));
                balance = underlying.balanceOfSelf();
            }
        }
        underlying.approve(address(vToken), 0);

        underlying.transferAll(receiver);
        Currency.wrap(address(vToken)).transferAll(receiver);
    }

    function batchRepayVAI(
        IVAIController vaiController,
        Repayment[] calldata requestedRepayments,
        address receiver
    ) external nonReentrant onlyOwner {
        vaiController.accrueVAIInterest();
        Currency vai = Currency.wrap(vaiController.getVAIAddress());
        uint256 balance = vai.balanceOfSelf();
        vai.approve(address(vaiController), type(uint256).max);
        uint256 repaymentsCount = requestedRepayments.length;
        for (uint256 i = 0; i < repaymentsCount && balance != 0; ++i) {
            Repayment calldata requestedRepayment = requestedRepayments[i];
            uint256 repaymentCap = requestedRepayment.amount;
            uint256 debt = vaiController.getVAIRepayAmount(requestedRepayment.borrower);
            uint256 amount = _min(repaymentCap, debt);
            _repayVAI(vaiController, requestedRepayment.borrower, _min(amount, balance));
            balance = vai.balanceOfSelf();
        }
        vai.approve(address(vaiController), 0);
        vai.transferAll(receiver);
    }

    function sweepTokens(address token, address destination) external onlyOwner {
        Currency.wrap(token).transferAll(destination);
    }

    function _accrueInterest(IVToken vToken) internal {
        uint256 err = vToken.accrueInterest();
        if (err != 0) {
            revert AccrueInterestFailed(err);
        }
    }

    function _redeemUpTo(IVToken vToken, uint256 amount) internal {
        uint256 unredeemedUnderlying = vToken.balanceOfUnderlying(address(this));
        if (unredeemedUnderlying > 0) {
            uint256 err = vToken.redeemUnderlying(_min(amount, unredeemedUnderlying));
            if (err != 0) {
                revert RedeemFailed(err);
            }
        }
    }

    function _repay(IVToken vToken, address borrower, uint256 amount) internal {
        if (amount == 0) {
            return;
        }
        if (_isVBNB(vToken)) {
            IVBNB(address(vToken)).repayBorrowBehalf{ value: amount }(borrower);
        } else {
            uint256 err = IVBep20(address(vToken)).repayBorrowBehalf(borrower, amount);
            if (err != 0) {
                revert RepaymentFailed(err);
            }
        }
    }

    function _repayVAI(IVAIController vaiController, address borrower, uint256 amount) internal {
        if (amount == 0) {
            return;
        }
        (uint256 err, ) = vaiController.repayVAIBehalf(borrower, amount);
        if (err != 0) {
            revert RepaymentFailed(err);
        }
    }

    function _getAmountsToRepay(
        IVToken vToken,
        Repayment[] calldata requestedRepayments
    ) internal view returns (uint256, Repayment[] memory) {
        uint256 repaymentsCount = requestedRepayments.length;
        Repayment[] memory actualRepayments = new Repayment[](repaymentsCount);
        uint256 totalAmountToRepay = 0;
        for (uint256 i = 0; i < repaymentsCount; ++i) {
            Repayment calldata requestedRepayment = requestedRepayments[i];
            uint256 repaymentCap = requestedRepayment.amount;
            uint256 debt = vToken.borrowBalanceStored(requestedRepayment.borrower);
            uint256 amountToRepay = _min(repaymentCap, debt);
            totalAmountToRepay += amountToRepay;
            actualRepayments[i] = Repayment({ borrower: requestedRepayment.borrower, amount: amountToRepay });
        }
        return (totalAmountToRepay, actualRepayments);
    }

    function _underlying(IVToken vToken) internal view returns (Currency) {
        if (_isVBNB(vToken)) {
            return CurrencyLibrary.NATIVE;
        }
        return Currency.wrap(IVBep20(address(vToken)).underlying());
    }

    function _isVBNB(IVToken vToken) internal view returns (bool) {
        return address(vToken) == address(VBNB);
    }

    function _min(uint256 a, uint256 b) internal pure returns (uint256) {
        return a < b ? a : b;
    }
}

contracts/Governance/VTreasuryV8.sol.content:
// SPDX-License-Identifier: BSD-3-Clause
pragma solidity 0.8.25;

import { SafeERC20, IERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { Ownable2Step } from "@openzeppelin/contracts/access/Ownable2Step.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * @title VTreasuryV8
 * @author Venus
 * @notice Protocol treasury that holds tokens owned by Venus
 */
contract VTreasuryV8 is Ownable2Step, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // WithdrawTreasuryToken Event
    event WithdrawTreasuryToken(address indexed tokenAddress, uint256 withdrawAmount, address indexed withdrawAddress);

    // WithdrawTreasuryNative Event
    event WithdrawTreasuryNative(uint256 withdrawAmount, address indexed withdrawAddress);

    /// @notice Thrown if the supplied address is a zero address where it is not allowed
    error ZeroAddressNotAllowed();

    /**
     * @notice To receive Native when msg.data is not empty
     */
    fallback() external payable {}

    /**
     * @notice To receive Native when msg.data is empty
     */
    receive() external payable {}

    /**
     * @notice Withdraw Treasury  Tokens, Only owner call it
     * @param tokenAddress The address of treasury token
     * @param withdrawAmount The withdraw amount to owner
     * @param withdrawAddress The withdraw address
     * @custom:error ZeroAddressNotAllowed thrown when token or withdrawAddress is zero.
     */
    function withdrawTreasuryToken(
        address tokenAddress,
        uint256 withdrawAmount,
        address withdrawAddress
    ) external onlyOwner nonReentrant {
        ensureNonzeroAddress(tokenAddress);
        ensureNonzeroAddress(withdrawAddress);
        require(withdrawAmount > 0, "withdrawAmount must not be zero");

        uint256 actualWithdrawAmount = withdrawAmount;
        // Get Treasury Token Balance
        uint256 treasuryBalance = IERC20(tokenAddress).balanceOf(address(this));

        // Check Withdraw Amount
        if (withdrawAmount > treasuryBalance) {
            // Update actualWithdrawAmount
            actualWithdrawAmount = treasuryBalance;
        }

        // Transfer Token to withdrawAddress
        IERC20(tokenAddress).safeTransfer(withdrawAddress, actualWithdrawAmount);

        emit WithdrawTreasuryToken(tokenAddress, actualWithdrawAmount, withdrawAddress);
    }

    /**
     * @notice Withdraw Treasury Native, Only owner call it
     * @param withdrawAmount The withdraw amount to owner
     * @param withdrawAddress The withdraw address
     * @custom:error ZeroAddressNotAllowed thrown when withdrawAddress is zero.
     */
    function withdrawTreasuryNative(
        uint256 withdrawAmount,
        address payable withdrawAddress
    ) external payable onlyOwner nonReentrant {
        ensureNonzeroAddress(withdrawAddress);
        require(withdrawAmount > 0, "withdrawAmount must not be zero");
        uint256 actualWithdrawAmount = withdrawAmount;
        // Get Treasury Native Balance
        uint256 nativeBalance = address(this).balance;

        // Check Withdraw Amount
        if (withdrawAmount > nativeBalance) {
            // Update actualWithdrawAmount
            actualWithdrawAmount = nativeBalance;
        }
        // Transfer the native token to withdrawAddress
        (bool sent, ) = withdrawAddress.call{ value: actualWithdrawAmount }("");
        require(sent, "Call failed");
        emit WithdrawTreasuryNative(actualWithdrawAmount, withdrawAddress);
    }

    /// @notice Checks if the provided address is nonzero, reverts otherwise
    /// @param address_ Address to check
    /// @custom:error ZeroAddressNotAllowed is thrown if the provided address is a zero address
    function ensureNonzeroAddress(address address_) internal pure {
        if (address_ == address(0)) {
            revert ZeroAddressNotAllowed();
        }
    }
}

