            revert InvalidArguments();
        }

        for (uint256 i; i < numTokens; ) {
            _initializeToken(tokens_[i]);
            _setMaxTokenDistributionSpeed(tokens_[i], maxDistributionSpeeds_[i]);
            _setTokenDistributionSpeed(tokens_[i], distributionSpeeds_[i]);

