// SPDX-License-Identifier: BSD-3-Clause
pragma solidity 0.8.25;
function c_8f1c8ff7(bytes8 c__8f1c8ff7) pure {}
function c_true8f1c8ff7(bytes8 c__8f1c8ff7) pure returns (bool){ return true; }
