// SPDX-License-Identifier: MIT
pragma solidity 0.8.25;

import { SafeERC20Upgradeable } from "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import { IERC20Upgradeable } from "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import { ReentrancyGuardUpgradeable } from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import { Ownable2StepUpgradeable } from "@openzeppelin/contracts-upgradeable/access/Ownable2StepUpgradeable.sol";
import { MANTISSA_ONE } from "@venusprotocol/solidity-utilities/contracts/constants.sol";
import { ensureNonzeroAddress } from "@venusprotocol/solidity-utilities/contracts/validators.sol";

import { approveOrRevert } from "../lib/approveOrRevert.sol";
import { ILiquidator, IComptroller, IVToken, IVBep20, IVBNB, IVAIController } from "../InterfacesV8.sol";

/**
 * @title BUSDLiquidator
 * @author Venus
 * @notice A custom contract for force-liquidating BUSD debts
 */
contract BUSDLiquidator is Ownable2StepUpgradeable, ReentrancyGuardUpgradeable {
    using SafeERC20Upgradeable for IERC20Upgradeable;
    using SafeERC20Upgradeable for IVToken;

    /// @custom:oz-upgrades-unsafe-allow state-variable-immutable
    IVBep20 public immutable vBUSD;

    /// @custom:oz-upgrades-unsafe-allow state-variable-immutable
    IComptroller public immutable comptroller;

    /// @custom:oz-upgrades-unsafe-allow state-variable-immutable
    address public immutable treasury;

    /// @notice The liquidator's share, scaled by 1e18 (e.g. 1.02 * 1e18 for 102% of the debt covered)
    uint256 public liquidatorShareMantissa;

    /// @notice Thrown if trying to set liquidator's share lower than 100% of the debt covered
    error LiquidatorShareTooLow(uint256 liquidatorShareMantissa_);

    /// @notice Thrown if trying to set liquidator's share larger than this contract can receive from a liquidation
    error LiquidatorShareTooHigh(uint256 maxLiquidatorShareMantissa, uint256 liquidatorShareMantissa_);

    /// @notice Emitted when the liquidator's share is set
    event NewLiquidatorShare(uint256 oldLiquidatorShareMantissa, uint256 newLiquidatorShareMantissa);

    /// @notice Constructor for the implementation contract. Sets immutable variables.
    /// @param comptroller_ The address of the Comptroller contract
    /// @param vBUSD_ The address of the VBNB
    /// @param treasury_ The address of Venus treasury
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor(address comptroller_, address vBUSD_, address treasury_) {
        ensureNonzeroAddress(vBUSD_);
        ensureNonzeroAddress(comptroller_);
        ensureNonzeroAddress(treasury_);
        vBUSD = IVBep20(vBUSD_);
        comptroller = IComptroller(comptroller_);
        treasury = treasury_;
        _disableInitializers();
    }

    /// @notice Initializer for the implementation contract.
    /// @param liquidatorShareMantissa_ Liquidator's share, scaled by 1e18 (e.g. 1.01 * 1e18 for 101%)
    /// @custom:error LiquidatorShareTooHigh is thrown if trying to set liquidator percent larger than the liquidation profit
    function initialize(uint256 liquidatorShareMantissa_) external virtual initializer {
        __Ownable2Step_init();
        __ReentrancyGuard_init();
        _validateLiquidatorShareMantissa(liquidatorShareMantissa_);
        liquidatorShareMantissa = liquidatorShareMantissa_;
    }

    /// @notice Liquidate the entire BUSD debt of a borrower, seizing vTokenCollateral
    /// @param borrower The borrower whose debt should be liquidated
    /// @param vTokenCollateral The collateral to seize from the borrower
    function liquidateEntireBorrow(address borrower, IVToken vTokenCollateral) external nonReentrant {
        uint256 repayAmount = vBUSD.borrowBalanceCurrent(borrower);
        _unpauseAndLiquidate(borrower, repayAmount, vTokenCollateral);
    }

    /// @notice Liquidate a BUSD borrow, repaying the repayAmount of BUSD
    /// @param borrower The borrower whose debt should be liquidated
    /// @param repayAmount The amount to repay
    /// @param vTokenCollateral The collateral to seize from the borrower
    function liquidateBorrow(address borrower, uint256 repayAmount, IVToken vTokenCollateral) external nonReentrant {
        _unpauseAndLiquidate(borrower, repayAmount, vTokenCollateral);
    }

    /// @notice Allows Governance to set the liquidator's share
    /// @param liquidatorShareMantissa_ Liquidator's share, scaled by 1e18 (e.g. 1.01 * 1e18 for 101%)
    /// @custom:access Only Governance
    function setLiquidatorShare(uint256 liquidatorShareMantissa_) external onlyOwner {
        _validateLiquidatorShareMantissa(liquidatorShareMantissa_);
        uint256 oldLiquidatorShareMantissa = liquidatorShareMantissa;
        liquidatorShareMantissa = liquidatorShareMantissa_;
        emit NewLiquidatorShare(oldLiquidatorShareMantissa, liquidatorShareMantissa_);
    }

    /// @notice Allows to recover token accidentally sent to this contract by sending the entire balance to Governance
    /// @param token The address of the token to recover
    /// @custom:access Only Governance
    function sweepToken(IERC20Upgradeable token) external onlyOwner {
        token.safeTransfer(msg.sender, token.balanceOf(address(this)));
    }

    /// @dev Unpauses the liquidation on the BUSD market, liquidates the borrower's debt,
    /// and pauses the liquidations back
    /// @param borrower The borrower whose debt should be liquidated
    /// @param repayAmount The amount to repay
    /// @param vTokenCollateral The collateral to seize from the borrower
    function _unpauseAndLiquidate(address borrower, uint256 repayAmount, IVToken vTokenCollateral) internal {
        address[] memory vTokens = new address[](1);
        vTokens[0] = address(vBUSD);
        IComptroller.Action[] memory actions = new IComptroller.Action[](1);
        actions[0] = IComptroller.Action.LIQUIDATE;

        comptroller._setActionsPaused(vTokens, actions, false);
        _liquidateBorrow(borrower, repayAmount, vTokenCollateral);
        comptroller._setActionsPaused(vTokens, actions, true);
    }

    /// @dev Performs the actual liquidation, transferring BUSD from the sender to this contract,
    /// repaying the debt, and transferring the seized collateral to the sender and the treasury
    /// @param borrower The borrower whose debt should be liquidated
    /// @param repayAmount The amount to repay
    /// @param vTokenCollateral The collateral to seize from the borrower
    function _liquidateBorrow(address borrower, uint256 repayAmount, IVToken vTokenCollateral) internal {
        ILiquidator liquidatorContract = ILiquidator(comptroller.liquidatorContract());
        IERC20Upgradeable busd = IERC20Upgradeable(vBUSD.underlying());

        uint256 actualRepayAmount = _transferIn(busd, msg.sender, repayAmount);
        approveOrRevert(busd, address(liquidatorContract), actualRepayAmount);
        uint256 balanceBefore = vTokenCollateral.balanceOf(address(this));
        liquidatorContract.liquidateBorrow(address(vBUSD), borrower, actualRepayAmount, vTokenCollateral);
        uint256 receivedAmount = vTokenCollateral.balanceOf(address(this)) - balanceBefore;
        approveOrRevert(busd, address(liquidatorContract), 0);

        (uint256 liquidatorAmount, uint256 treasuryAmount) = _computeShares(receivedAmount);
        vTokenCollateral.safeTransfer(msg.sender, liquidatorAmount);
        vTokenCollateral.safeTransfer(treasury, treasuryAmount);
    }

    /// @dev Transfers tokens to this contract and returns the actual transfer amount
    /// @param token The token to transfer
    /// @param from The account to transfer from
    /// @param amount The amount to transfer
    /// @return The actual amount transferred
    function _transferIn(IERC20Upgradeable token, address from, uint256 amount) internal returns (uint256) {
        uint256 prevBalance = token.balanceOf(address(this));
        token.safeTransferFrom(from, address(this), amount);
        return token.balanceOf(address(this)) - prevBalance;
    }

    /// @dev Computes the liquidator's and treasury's shares of the received amount
    /// @param receivedAmount The amount received from the liquidation
    /// @return liquidatorAmount The liquidator's share
    /// @return treasuryAmount The treasury's share
    function _computeShares(
        uint256 receivedAmount
    ) internal view returns (uint256 liquidatorAmount, uint256 treasuryAmount) {
        uint256 denominator = _getDenominator();
        liquidatorAmount = (receivedAmount * liquidatorShareMantissa) / denominator;
        treasuryAmount = receivedAmount - liquidatorAmount;
    }

    /// @dev Returns (liquidation incentive - treasury percent), the value used as the denominator
    /// when calculating the liquidator's share
    /// @return The denominator for the seized amount used when calculating the liquidator's share
    function _getDenominator() internal view returns (uint256) {
        uint256 totalPercentageToDistribute = comptroller.liquidationIncentiveMantissa();
        uint256 regularTreasuryPercent = ILiquidator(comptroller.liquidatorContract()).treasuryPercentMantissa();
        uint256 denominator = totalPercentageToDistribute - regularTreasuryPercent;
        return denominator;
    }

    /// @dev Checks if the liquidator's share is more than 100% of the debt covered and less
    /// than (liquidation incentive - treasury percent)
    /// @param liquidatorShareMantissa_ Liquidator's share, scaled by 1e18 (e.g. 1.01 * 1e18 for 101%)
    function _validateLiquidatorShareMantissa(uint256 liquidatorShareMantissa_) internal view {
        uint256 maxLiquidatorShareMantissa = _getDenominator();
        if (liquidatorShareMantissa_ < MANTISSA_ONE) {
            revert LiquidatorShareTooLow(liquidatorShareMantissa_);
        }

        if (liquidatorShareMantissa_ > maxLiquidatorShareMantissa) {
            revert LiquidatorShareTooHigh(maxLiquidatorShareMantissa, liquidatorShareMantissa_);
        }
    }
}

contracts/Liquidator/Liquidator.sol.content:
// SPDX-License-Identifier: BSD-3-Clause
pragma solidity 0.8.25;

import { ReentrancyGuardUpgradeable } from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import { Ownable2StepUpgradeable } from "@openzeppelin/contracts-upgradeable/access/Ownable2StepUpgradeable.sol";
import { IERC20Upgradeable } from "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import { SafeERC20Upgradeable } from "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import { ensureNonzeroAddress } from "@venusprotocol/solidity-utilities/contracts/validators.sol";
import { AccessControlledV8 } from "@venusprotocol/governance-contracts/contracts/Governance/AccessControlledV8.sol";
import { IProtocolShareReserve } from "../external/IProtocolShareReserve.sol";
import { IWBNB } from "../external/IWBNB.sol";
import { LiquidatorStorage } from "./LiquidatorStorage.sol";
import { IComptroller, IVToken, IVBep20, IVBNB, IVAIController } from "../InterfacesV8.sol";

contract Liquidator is Ownable2StepUpgradeable, ReentrancyGuardUpgradeable, LiquidatorStorage, AccessControlledV8 {
    /// @notice Address of vBNB contract.
    /// @custom:oz-upgrades-unsafe-allow state-variable-immutable
    IVBNB public immutable vBnb;

    /// @notice Address of Venus Unitroller contract.
    /// @custom:oz-upgrades-unsafe-allow state-variable-immutable
    IComptroller public immutable comptroller;

    /// @notice Address of VAIUnitroller contract.
    /// @custom:oz-upgrades-unsafe-allow state-variable-immutable
    IVAIController public immutable vaiController;

    /// @notice Address of wBNB contract
    /// @custom:oz-upgrades-unsafe-allow state-variable-immutable
    address public immutable wBNB;

    /// @dev A unit (literal one) in EXP_SCALE, usually used in additions/subtractions
    uint256 internal constant MANTISSA_ONE = 1e18;

    /* Events */

    /// @notice Emitted when the percent of the seized amount that goes to treasury changes.
    event NewLiquidationTreasuryPercent(uint256 oldPercent, uint256 newPercent);

    /// @notice Emitted when a borrow is liquidated
    event LiquidateBorrowedTokens(
        address indexed liquidator,
        address indexed borrower,
        uint256 repayAmount,
        address vTokenBorrowed,
        address indexed vTokenCollateral,
        uint256 seizeTokensForTreasury,
        uint256 seizeTokensForLiquidator
    );

    /// @notice Emitted when the liquidation is restricted for a borrower
    event LiquidationRestricted(address indexed borrower);

    /// @notice Emitted when the liquidation restrictions are removed for a borrower
    event LiquidationRestrictionsDisabled(address indexed borrower);

    /// @notice Emitted when a liquidator is added to the allowedLiquidatorsByAccount mapping
    event AllowlistEntryAdded(address indexed borrower, address indexed liquidator);

    /// @notice Emitted when a liquidator is removed from the allowedLiquidatorsByAccount mapping
    event AllowlistEntryRemoved(address indexed borrower, address indexed liquidator);

    /// @notice Emitted when the amount of minLiquidatableVAI is updated
    event NewMinLiquidatableVAI(uint256 oldMinLiquidatableVAI, uint256 newMinLiquidatableVAI);

    /// @notice Emitted when the length of chunk gets updated
    event NewPendingRedeemChunkLength(uint256 oldPendingRedeemChunkLength, uint256 newPendingRedeemChunkLength);

    /// @notice Emitted when force liquidation is paused
    event ForceVAILiquidationPaused(address indexed sender);

    /// @notice Emitted when force liquidation is resumed
    event ForceVAILiquidationResumed(address indexed sender);

    /// @notice Emitted when new address of protocol share reserve is set
    event NewProtocolShareReserve(address indexed oldProtocolShareReserve, address indexed newProtocolShareReserves);

    /// @notice Emitted when reserves are reduced from liquidator contract to protocol share reserves
    event ProtocolLiquidationIncentiveTransferred(address indexed sender, address indexed token, uint256 reducedAmount);

    /* Errors */

    /// @notice Thrown if the liquidation is restricted and the liquidator is not in the allowedLiquidatorsByAccount mapping
    error LiquidationNotAllowed(address borrower, address liquidator);

    /// @notice Thrown if VToken transfer fails after the liquidation
    error VTokenTransferFailed(address from, address to, uint256 amount);

    /// @notice Thrown if the liquidation is not successful (the error code is from TokenErrorReporter)
    error LiquidationFailed(uint256 errorCode);

    /// @notice Thrown if trying to restrict liquidations for an already restricted borrower
    error AlreadyRestricted(address borrower);

    /// @notice Thrown if trying to unrestrict liquidations for a borrower that is not restricted
    error NoRestrictionsExist(address borrower);

    /// @notice Thrown if the liquidator is already in the allowedLiquidatorsByAccount mapping
    error AlreadyAllowed(address borrower, address liquidator);

    /// @notice Thrown if trying to remove a liquidator that is not in the allowedLiquidatorsByAccount mapping
    error AllowlistEntryNotFound(address borrower, address liquidator);

    /// @notice Thrown if BNB amount sent with the transaction doesn't correspond to the
    ///         intended BNB repayment
    error WrongTransactionAmount(uint256 expected, uint256 actual);

    /// @notice Thrown if trying to set treasury percent larger than the liquidation profit
    error TreasuryPercentTooHigh(uint256 maxTreasuryPercentMantissa, uint256 treasuryPercentMantissa_);

    /// @notice Thrown if trying to liquidate any token when VAI debt is too high
    error VAIDebtTooHigh(uint256 vaiDebt, uint256 minLiquidatableVAI);

    /// @notice Thrown when vToken is not listed
    error MarketNotListed(address vToken);

    using SafeERC20Upgradeable for IERC20Upgradeable;

    /// @notice Constructor for the implementation contract. Sets immutable variables.
    /// @param comptroller_ The address of the Comptroller contract
    /// @param vBnb_ The address of the VBNB
    /// @param wBNB_ The address of wBNB
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor(address comptroller_, address payable vBnb_, address wBNB_) {
        ensureNonzeroAddress(vBnb_);
        ensureNonzeroAddress(comptroller_);
        ensureNonzeroAddress(wBNB_);
        vBnb = IVBNB(vBnb_);
        wBNB = wBNB_;
        comptroller = IComptroller(comptroller_);
        vaiController = IVAIController(IComptroller(comptroller_).vaiController());
        _disableInitializers();
    }

    receive() external payable {}

    /// @notice Initializer for the implementation contract.
    /// @param treasuryPercentMantissa_ Treasury share, scaled by 1e18 (e.g. 0.2 * 1e18 for 20%)
    /// @param accessControlManager_ address of access control manager
    /// @param protocolShareReserve_ The address of the protocol share reserve contract
    function initialize(
        uint256 treasuryPercentMantissa_,
        address accessControlManager_,
        address protocolShareReserve_
    ) external virtual reinitializer(2) {
        __Liquidator_init(treasuryPercentMantissa_, accessControlManager_, protocolShareReserve_);
    }

    /// @dev Liquidator initializer for derived contracts.
    /// @param treasuryPercentMantissa_ Treasury share, scaled by 1e18 (e.g. 0.2 * 1e18 for 20%)
    /// @param accessControlManager_ address of access control manager
    /// @param protocolShareReserve_ The address of the protocol share reserve contract
    function __Liquidator_init(
        uint256 treasuryPercentMantissa_,
        address accessControlManager_,
        address protocolShareReserve_
    ) internal onlyInitializing {
        __Ownable2Step_init();
        __ReentrancyGuard_init();
        __Liquidator_init_unchained(treasuryPercentMantissa_, protocolShareReserve_);
        __AccessControlled_init_unchained(accessControlManager_);
    }

    /// @dev Liquidator initializer for derived contracts that doesn't call parent initializers.
    /// @param treasuryPercentMantissa_ Treasury share, scaled by 1e18 (e.g. 0.2 * 1e18 for 20%)
    /// @param protocolShareReserve_ The address of the protocol share reserve contract
    function __Liquidator_init_unchained(
        uint256 treasuryPercentMantissa_,
        address protocolShareReserve_
    ) internal onlyInitializing {
        validateTreasuryPercentMantissa(treasuryPercentMantissa_);
        treasuryPercentMantissa = treasuryPercentMantissa_;
        _setProtocolShareReserve(protocolShareReserve_);
    }

    /// @notice An admin function to restrict liquidations to allowed addresses only.
    /// @dev Use {addTo,removeFrom}AllowList to configure the allowed addresses.
    /// @param borrower The address of the borrower
    function restrictLiquidation(address borrower) external {
        _checkAccessAllowed("restrictLiquidation(address)");
        if (liquidationRestricted[borrower]) {
            revert AlreadyRestricted(borrower);
        }
        liquidationRestricted[borrower] = true;
        emit LiquidationRestricted(borrower);
    }

    /// @notice An admin function to remove restrictions for liquidations.
    /// @dev Does not impact the allowedLiquidatorsByAccount mapping for the borrower, just turns off the check.
    /// @param borrower The address of the borrower
    function unrestrictLiquidation(address borrower) external {
        _checkAccessAllowed("unrestrictLiquidation(address)");
        if (!liquidationRestricted[borrower]) {
            revert NoRestrictionsExist(borrower);
        }
        liquidationRestricted[borrower] = false;
        emit LiquidationRestrictionsDisabled(borrower);
    }

    /// @notice An admin function to add the liquidator to the allowedLiquidatorsByAccount mapping for a certain
    ///         borrower. If the liquidations are restricted, only liquidators from the
    ///         allowedLiquidatorsByAccount mapping can participate in liquidating the positions of this borrower.
    /// @param borrower The address of the borrower
    /// @param borrower The address of the liquidator
    function addToAllowlist(address borrower, address liquidator) external {
        _checkAccessAllowed("addToAllowlist(address,address)");
        if (allowedLiquidatorsByAccount[borrower][liquidator]) {
            revert AlreadyAllowed(borrower, liquidator);
        }
        allowedLiquidatorsByAccount[borrower][liquidator] = true;
        emit AllowlistEntryAdded(borrower, liquidator);
    }

    /// @notice An admin function to remove the liquidator from the allowedLiquidatorsByAccount mapping of a certain
    ///         borrower. If the liquidations are restricted, this liquidator will not be
    ///         able to liquidate the positions of this borrower.
    /// @param borrower The address of the borrower
    /// @param borrower The address of the liquidator
    function removeFromAllowlist(address borrower, address liquidator) external {
        _checkAccessAllowed("removeFromAllowlist(address,address)");
        if (!allowedLiquidatorsByAccount[borrower][liquidator]) {
            revert AllowlistEntryNotFound(borrower, liquidator);
        }
        allowedLiquidatorsByAccount[borrower][liquidator] = false;
        emit AllowlistEntryRemoved(borrower, liquidator);
    }

    /// @notice Liquidates a borrow and splits the seized amount between protocol share reserve and
    ///         liquidator. The liquidators should use this interface instead of calling
    ///         vToken.liquidateBorrow(...) directly.
    /// @notice Checks force VAI liquidation first; vToken should be address of vaiController if vaiDebt is greater than threshold
    /// @notice For BNB borrows msg.value should be equal to repayAmount; otherwise msg.value
    ///      should be zero.
    /// @param vToken Borrowed vToken
    /// @param borrower The address of the borrower
    /// @param repayAmount The amount to repay on behalf of the borrower
    /// @param vTokenCollateral The collateral to seize
    function liquidateBorrow(
        address vToken,
        address borrower,
        uint256 repayAmount,
        IVToken vTokenCollateral
    ) external payable nonReentrant {
        ensureNonzeroAddress(borrower);
        checkRestrictions(borrower, msg.sender);
        (bool isListed, , ) = IComptroller(comptroller).markets(address(vTokenCollateral));
        if (!isListed) {
            revert MarketNotListed(address(vTokenCollateral));
        }

        _checkForceVAILiquidate(vToken, borrower);
        uint256 ourBalanceBefore = vTokenCollateral.balanceOf(address(this));
        if (vToken == address(vBnb)) {
            if (repayAmount != msg.value) {
                revert WrongTransactionAmount(repayAmount, msg.value);
            }
            vBnb.liquidateBorrow{ value: msg.value }(borrower, vTokenCollateral);
        } else {
            if (msg.value != 0) {
                revert WrongTransactionAmount(0, msg.value);
            }
            if (vToken == address(vaiController)) {
                _liquidateVAI(borrower, repayAmount, vTokenCollateral);
            } else {
                _liquidateBep20(IVBep20(vToken), borrower, repayAmount, vTokenCollateral);
            }
        }
        uint256 ourBalanceAfter = vTokenCollateral.balanceOf(address(this));
        uint256 seizedAmount = ourBalanceAfter - ourBalanceBefore;
        (uint256 ours, uint256 theirs) = _distributeLiquidationIncentive(vTokenCollateral, seizedAmount);
        _reduceReservesInternal();
        emit LiquidateBorrowedTokens(
            msg.sender,
            borrower,
            repayAmount,
            vToken,
            address(vTokenCollateral),
            ours,
            theirs
        );
    }

    /// @notice Sets the new percent of the seized amount that goes to treasury. Should
    ///         be less than or equal to comptroller.liquidationIncentiveMantissa().sub(1e18).
    /// @param newTreasuryPercentMantissa New treasury percent (scaled by 10^18).
    function setTreasuryPercent(uint256 newTreasuryPercentMantissa) external {
        _checkAccessAllowed("setTreasuryPercent(uint256)");
        validateTreasuryPercentMantissa(newTreasuryPercentMantissa);
        emit NewLiquidationTreasuryPercent(treasuryPercentMantissa, newTreasuryPercentMantissa);
        treasuryPercentMantissa = newTreasuryPercentMantissa;
    }

    /**
     * @notice Sets protocol share reserve contract address
     * @param protocolShareReserve_ The address of the protocol share reserve contract
     */
    function setProtocolShareReserve(address payable protocolShareReserve_) external onlyOwner {
        _setProtocolShareReserve(protocolShareReserve_);
    }

    /**
     * @notice Reduce the reserves of the pending accumulated reserves
     */
    function reduceReserves() external nonReentrant {
        _reduceReservesInternal();
    }

    function _reduceReservesInternal() internal {
        uint256 _pendingRedeemLength = pendingRedeem.length;
        uint256 range = _pendingRedeemLength >= pendingRedeemChunkLength
            ? pendingRedeemChunkLength
            : _pendingRedeemLength;
        for (uint256 index = range; index > 0; ) {
            address vToken = pendingRedeem[index - 1];
            uint256 vTokenBalance_ = IVToken(vToken).balanceOf(address(this));
            if (_redeemUnderlying(vToken, vTokenBalance_)) {
                if (vToken == address(vBnb)) {
                    _reduceBnbReserves();
                } else {
                    _reduceVTokenReserves(vToken);
                }
                pendingRedeem[index - 1] = pendingRedeem[pendingRedeem.length - 1];
                pendingRedeem.pop();
            }
            unchecked {
                index--;
            }
        }
    }

    /// @dev Transfers BEP20 tokens to self, then approves vToken to take these tokens.
    function _liquidateBep20(IVBep20 vToken, address borrower, uint256 repayAmount, IVToken vTokenCollateral) internal {
        (bool isListed, , ) = IComptroller(comptroller).markets(address(vToken));
        if (!isListed) {
            revert MarketNotListed(address(vToken));
        }

        IERC20Upgradeable borrowedToken = IERC20Upgradeable(vToken.underlying());
        uint256 actualRepayAmount = _transferBep20(borrowedToken, msg.sender, address(this), repayAmount);
        borrowedToken.safeApprove(address(vToken), 0);
        borrowedToken.safeApprove(address(vToken), actualRepayAmount);
        requireNoError(vToken.liquidateBorrow(borrower, actualRepayAmount, vTokenCollateral));
    }

    /// @dev Transfers BEP20 tokens to self, then approves VAI to take these tokens.
    function _liquidateVAI(address borrower, uint256 repayAmount, IVToken vTokenCollateral) internal {
        IERC20Upgradeable vai = IERC20Upgradeable(vaiController.getVAIAddress());
        vai.safeTransferFrom(msg.sender, address(this), repayAmount);
        vai.safeApprove(address(vaiController), 0);
        vai.safeApprove(address(vaiController), repayAmount);

        (uint256 err, ) = vaiController.liquidateVAI(borrower, repayAmount, vTokenCollateral);
        requireNoError(err);
    }

    /// @dev Distribute seized collateral between liquidator and protocol share reserve
    function _distributeLiquidationIncentive(
        IVToken vTokenCollateral,
        uint256 seizedAmount
    ) internal returns (uint256 ours, uint256 theirs) {
        (ours, theirs) = _splitLiquidationIncentive(seizedAmount);
        if (!vTokenCollateral.transfer(msg.sender, theirs)) {
            revert VTokenTransferFailed(address(this), msg.sender, theirs);
        }

        if (ours > 0 && !_redeemUnderlying(address(vTokenCollateral), ours)) {
            // Check if asset is already present in pendingRedeem array
            uint256 index;
            for (index; index < pendingRedeem.length; ) {
                if (pendingRedeem[index] == address(vTokenCollateral)) {
                    break;
                }
                unchecked {
                    index++;
                }
            }
            if (index == pendingRedeem.length) {
                pendingRedeem.push(address(vTokenCollateral));
            }
        } else {
            if (address(vTokenCollateral) == address(vBnb)) {
                _reduceBnbReserves();
            } else {
                _reduceVTokenReserves(address(vTokenCollateral));
            }
        }
    }

    /// @dev Wraps BNB to wBNB and sends to protocol share reserve
    function _reduceBnbReserves() private {
        uint256 bnbBalance = address(this).balance;
        IWBNB(wBNB).deposit{ value: bnbBalance }();
        IERC20Upgradeable(wBNB).safeTransfer(protocolShareReserve, bnbBalance);
        IProtocolShareReserve(protocolShareReserve).updateAssetsState(
            address(comptroller),
            wBNB,
            IProtocolShareReserve.IncomeType.LIQUIDATION
        );
        emit ProtocolLiquidationIncentiveTransferred(msg.sender, wBNB, bnbBalance);
    }

    /// @dev Redeem seized collateral to underlying assets
    function _redeemUnderlying(address vToken, uint256 amount) private returns (bool) {
        try IVToken(address(vToken)).redeem(amount) returns (uint256 response) {
            if (response == 0) {
                return true;
            } else {
                return false;
            }
        } catch {
            return false;
        }
    }

    /// @dev Transfers seized collateral other than BNB to protocol share reserve
    function _reduceVTokenReserves(address vToken) private {
        address underlying = IVBep20(vToken).underlying();
        uint256 underlyingBalance = IERC20Upgradeable(underlying).balanceOf(address(this));
        IERC20Upgradeable(underlying).safeTransfer(protocolShareReserve, underlyingBalance);
        IProtocolShareReserve(protocolShareReserve).updateAssetsState(
            address(comptroller),
            underlying,
            IProtocolShareReserve.IncomeType.LIQUIDATION
        );
        emit ProtocolLiquidationIncentiveTransferred(msg.sender, underlying, underlyingBalance);
    }

    /// @dev Transfers tokens and returns the actual transfer amount
    function _transferBep20(
        IERC20Upgradeable token,
        address from,
        address to,
        uint256 amount
    ) internal returns (uint256) {
        uint256 prevBalance = token.balanceOf(to);
        token.safeTransferFrom(from, to, amount);
        return token.balanceOf(to) - prevBalance;
    }

    /// @dev Computes the amounts that would go to treasury and to the liquidator.
    function _splitLiquidationIncentive(uint256 seizedAmount) internal view returns (uint256 ours, uint256 theirs) {
        uint256 totalIncentive = comptroller.liquidationIncentiveMantissa();
        ours = (seizedAmount * treasuryPercentMantissa) / totalIncentive;
        theirs = seizedAmount - ours;
    }

    function requireNoError(uint256 errCode) internal pure {
        if (errCode == uint256(0)) {
            return;
        }

        revert LiquidationFailed(errCode);
    }

    function checkRestrictions(address borrower, address liquidator) internal view {
        if (liquidationRestricted[borrower] && !allowedLiquidatorsByAccount[borrower][liquidator]) {
            revert LiquidationNotAllowed(borrower, liquidator);
        }
    }

    function validateTreasuryPercentMantissa(uint256 treasuryPercentMantissa_) internal view {
        uint256 maxTreasuryPercentMantissa = comptroller.liquidationIncentiveMantissa() - MANTISSA_ONE;
        if (treasuryPercentMantissa_ > maxTreasuryPercentMantissa) {
            revert TreasuryPercentTooHigh(maxTreasuryPercentMantissa, treasuryPercentMantissa_);
        }
    }

    /// @dev Checks liquidation action in comptroller and vaiDebt with minLiquidatableVAI threshold
    function _checkForceVAILiquidate(address vToken_, address borrower_) private view {
        uint256 _vaiDebt = vaiController.getVAIRepayAmount(borrower_);
        bool _isVAILiquidationPaused = comptroller.actionPaused(address(vaiController), IComptroller.Action.LIQUIDATE);
        bool _isForcedLiquidationEnabled = comptroller.isForcedLiquidationEnabled(vToken_);
        if (
            _isForcedLiquidationEnabled ||
            _isVAILiquidationPaused ||
            !forceVAILiquidate ||
            _vaiDebt < minLiquidatableVAI ||
            vToken_ == address(vaiController)
        ) return;
        revert VAIDebtTooHigh(_vaiDebt, minLiquidatableVAI);
    }

    function _setProtocolShareReserve(address protocolShareReserve_) internal {
        ensureNonzeroAddress(protocolShareReserve_);
        emit NewProtocolShareReserve(protocolShareReserve, protocolShareReserve_);
        protocolShareReserve = protocolShareReserve_;
    }

    /**
     * @notice Sets the threshold for minimum amount of vaiLiquidate
     * @param minLiquidatableVAI_ New address for the access control
     */
    function setMinLiquidatableVAI(uint256 minLiquidatableVAI_) external {
        _checkAccessAllowed("setMinLiquidatableVAI(uint256)");
        emit NewMinLiquidatableVAI(minLiquidatableVAI, minLiquidatableVAI_);
        minLiquidatableVAI = minLiquidatableVAI_;
    }

    /**
     * @notice Length of the pendingRedeem array to be consider while redeeming in Liquidation transaction
     * @param newLength_ Length of the chunk
     */
    function setPendingRedeemChunkLength(uint256 newLength_) external {
        _checkAccessAllowed("setPendingRedeemChunkLength(uint256)");
        require(newLength_ > 0, "Invalid chunk size");
        emit NewPendingRedeemChunkLength(pendingRedeemChunkLength, newLength_);
        pendingRedeemChunkLength = newLength_;
    }

    /**
     * @notice Pause Force Liquidation of VAI
     */
    function pauseForceVAILiquidate() external {
        _checkAccessAllowed("pauseForceVAILiquidate()");
        require(forceVAILiquidate, "Force Liquidation of VAI is already Paused");
        forceVAILiquidate = false;
        emit ForceVAILiquidationPaused(msg.sender);
    }

    /**
     * @notice Resume Force Liquidation of VAI
     */
    function resumeForceVAILiquidate() external {
        _checkAccessAllowed("resumeForceVAILiquidate()");
        require(!forceVAILiquidate, "Force Liquidation of VAI is already resumed");
        forceVAILiquidate = true;
        emit ForceVAILiquidationResumed(msg.sender);
    }

    function renounceOwnership() public override {}
}

contracts/Liquidator/LiquidatorStorage.s.content:
// SPDX-License-Identifier: BSD-3-Clause
pragma solidity 0.8.25;

contract LiquidatorStorage {
    /* State */

    /// @notice Percent of seized amount that goes to treasury.
    uint256 public treasuryPercentMantissa;

    /// @notice Mapping of addresses allowed to liquidate an account if liquidationRestricted[borrower] == true
    mapping(address => mapping(address => bool)) public allowedLiquidatorsByAccount;

    /// @notice Whether the liquidations are restricted to enabled allowedLiquidatorsByAccount addresses only
    mapping(address => bool) public liquidationRestricted;

    /// @notice minimum amount of VAI liquidation threshold
    uint256 public minLiquidatableVAI;

    /// @notice check for liquidation of VAI
    bool public forceVAILiquidate;

    /// @notice assests whose redeem is pending to reduce reserves
    address[] public pendingRedeem;

    /// @notice protocol share reserve contract address
    address public protocolShareReserve;

    /// @dev Size of chunk to consider when redeeming underlying at the time of liquidation
    uint256 internal pendingRedeemChunkLength;

    /// @notice gap to prevent collision in inheritance
    uint256[49] private __gap;
}

contracts/PegStability/IVAI.sol.content:
// SPDX-License-Identifier: BSD-3-Clause
pragma solidity ^0.8.25;

interface IVAI {
    function balanceOf(address usr) external returns (uint256);

    function transferFrom(address src, address dst, uint amount) external returns (bool);

    function mint(address usr, uint wad) external;

    function burn(address usr, uint wad) external;
}

contracts/PegStability/PegStability.sol.content:
// SPDX-License-Identifier: BSD-3-Clause
pragma solidity 0.8.25;

import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/IERC20MetadataUpgradeable.sol";
import "@venusprotocol/governance-contracts/contracts/Governance/AccessControlledV8.sol";
import { ResilientOracleInterface } from "@venusprotocol/oracle/contracts/interfaces/OracleInterface.sol";
import { IVAI } from "./IVAI.sol";

/**
 * @title Peg Stability Contract.
 * @notice Contract for swapping stable token for VAI token and vice versa to maintain the peg stability between them.
 * @author Venus Protocol
 */
contract PegStability is AccessControlledV8, ReentrancyGuardUpgradeable {
    using SafeERC20Upgradeable for IERC20Upgradeable;

    // Helper enum for calculation of the fee.
    enum FeeDirection {
        IN,
        OUT
    }

    /// @notice The divisor used to convert fees to basis points.
    uint256 public constant BASIS_POINTS_DIVISOR = 10000;

    /// @notice The mantissa value representing 1 (used for calculations).
    uint256 public constant MANTISSA_ONE = 1e18;

    /// @notice The value representing one dollar in the stable token.
    /// @dev Our oracle is returning amount depending on the number of decimals of the stable token. (36 - asset_decimals) E.g. 8 decimal asset = 1e28.
    /// @custom:oz-upgrades-unsafe-allow state-variable-immutable
    uint256 public immutable ONE_DOLLAR;

    /// @notice VAI token contract.
    /// @custom:oz-upgrades-unsafe-allow state-variable-immutable
    IVAI public immutable VAI;

    /// @notice The address of the stable token contract.
    /// @custom:oz-upgrades-unsafe-allow state-variable-immutable
    address public immutable STABLE_TOKEN_ADDRESS;

    /// @notice The address of ResilientOracle contract wrapped in its interface.
    ResilientOracleInterface public oracle;

    /// @notice The address of the Venus Treasury contract.
    address public venusTreasury;

    /// @notice The incoming stableCoin fee. (Fee for swapStableForVAI).
    uint256 public feeIn;

    /// @notice The outgoing stableCoin fee. (Fee for swapVAIForStable).
    uint256 public feeOut;

    /// @notice The maximum amount of VAI that can be minted through this contract.
    uint256 public vaiMintCap;

    /// @notice The total amount of VAI minted through this contract.
    uint256 public vaiMinted;

    /// @notice A flag indicating whether the contract is currently paused or not.
    bool public isPaused;

    /// @notice Event emitted when contract is paused.
    event PSMPaused(address indexed admin);

    /// @notice Event emitted when the contract is resumed after pause.
    event PSMResumed(address indexed admin);

    /// @notice Event emitted when feeIn state var is modified.
    event FeeInChanged(uint256 oldFeeIn, uint256 newFeeIn);

    /// @notice Event emitted when feeOut state var is modified.
    event FeeOutChanged(uint256 oldFeeOut, uint256 newFeeOut);

    /// @notice Event emitted when vaiMintCap state var is modified.
    event VAIMintCapChanged(uint256 oldCap, uint256 newCap);

    /// @notice Event emitted when venusTreasury state var is modified.
    event VenusTreasuryChanged(address indexed oldTreasury, address indexed newTreasury);

    /// @notice Event emitted when oracle state var is modified.
    event OracleChanged(address indexed oldOracle, address indexed newOracle);

    /// @notice Event emitted when stable token is swapped for VAI.
    event StableForVAISwapped(uint256 stableIn, uint256 vaiOut, uint256 fee);

    /// @notice Event emitted when stable token is swapped for VAI.
    event VAIForStableSwapped(uint256 vaiBurnt, uint256 stableOut, uint256 vaiFee);

    /// @notice thrown when contract is in paused state
    error Paused();

    /// @notice thrown when attempted to pause an already paused contract
    error AlreadyPaused();

    /// @notice thrown when attempted to resume the contract if it is already resumed
    error NotPaused();

    /// @notice thrown when stable token has more than 18 decimals
    error TooManyDecimals();

    /// @notice thrown when fee is >= 100%
    error InvalidFee();

    /// @notice thrown when a zero address is passed as a function parameter
    error ZeroAddress();

    /// @notice thrown when a zero amount is passed as stable token amount parameter
    error ZeroAmount();

    /// @notice thrown when the user doesn't have enough VAI balance to provide for the amount of stable tokens he wishes to get
    error NotEnoughVAI();

    /// @notice thrown when the amount of VAI to be burnt exceeds the vaiMinted amount
    error VAIMintedUnderflow();

    /// @notice thrown when the VAI transfer to treasury fails
    error VAITransferFail();

    /// @notice thrown when VAI to be minted will go beyond the mintCap threshold
    error VAIMintCapReached();
    /// @notice thrown when fee calculation will result in rounding down to 0 due to stable token amount being a too small number
    error AmountTooSmall();

    /**
     * @dev Prevents functions to execute when contract is paused.
     */
    modifier isActive() {
        if (isPaused) revert Paused();
        _;
    }

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor(address stableTokenAddress_, address vaiAddress_) {
        _ensureNonzeroAddress(stableTokenAddress_);
        _ensureNonzeroAddress(vaiAddress_);

        uint256 decimals_ = IERC20MetadataUpgradeable(stableTokenAddress_).decimals();

        if (decimals_ > 18) {
            revert TooManyDecimals();
        }

        ONE_DOLLAR = 10 ** (36 - decimals_); // 1$ scaled to the decimals returned by our Oracle
        STABLE_TOKEN_ADDRESS = stableTokenAddress_;
        VAI = IVAI(vaiAddress_);
        _disableInitializers();
    }

    /**
     * @notice Initializes the contract via Proxy Contract with the required parameters.
     * @param accessControlManager_ The address of the AccessControlManager contract.
     * @param venusTreasury_ The address where fees will be sent.
     * @param oracleAddress_ The address of the ResilientOracle contract.
     * @param feeIn_ The percentage of fees to be applied to a stablecoin -> VAI swap.
     * @param feeOut_ The percentage of fees to be applied to a VAI -> stablecoin swap.
     * @param vaiMintCap_ The cap for the total amount of VAI that can be minted.
     */
    function initialize(
        address accessControlManager_,
        address venusTreasury_,
        address oracleAddress_,
        uint256 feeIn_,
        uint256 feeOut_,
        uint256 vaiMintCap_
    ) external initializer {
        _ensureNonzeroAddress(accessControlManager_);
        _ensureNonzeroAddress(venusTreasury_);
        _ensureNonzeroAddress(oracleAddress_);
        __AccessControlled_init(accessControlManager_);
        __ReentrancyGuard_init();

        if (feeIn_ >= BASIS_POINTS_DIVISOR || feeOut_ >= BASIS_POINTS_DIVISOR) {
            revert InvalidFee();
        }

        feeIn = feeIn_;
        feeOut = feeOut_;
        vaiMintCap = vaiMintCap_;
        venusTreasury = venusTreasury_;
        oracle = ResilientOracleInterface(oracleAddress_);
    }

    /*** Swap Functions ***/

    /**
     * @notice Swaps VAI for a stable token.
     * @param receiver The address where the stablecoin will be sent.
     * @param stableTknAmount The amount of stable tokens to receive.
     * @return The amount of VAI received and burnt from the sender.
     */
    // @custom:event Emits VAIForStableSwapped event.
    function swapVAIForStable(
        address receiver,
        uint256 stableTknAmount
    ) external isActive nonReentrant returns (uint256) {
        _ensureNonzeroAddress(receiver);
        _ensureNonzeroAmount(stableTknAmount);

        // update oracle price and calculate USD value of the stable token amount scaled in 18 decimals
        oracle.updateAssetPrice(STABLE_TOKEN_ADDRESS);
        uint256 stableTknAmountUSD = _previewTokenUSDAmount(stableTknAmount, FeeDirection.OUT);
        uint256 fee = _calculateFee(stableTknAmountUSD, FeeDirection.OUT);

        if (VAI.balanceOf(msg.sender) < stableTknAmountUSD + fee) {
            revert NotEnoughVAI();
        }
        if (vaiMinted < stableTknAmountUSD) {
            revert VAIMintedUnderflow();
        }

        unchecked {
            vaiMinted -= stableTknAmountUSD;
        }

        if (fee != 0) {
            bool success = VAI.transferFrom(msg.sender, venusTreasury, fee);
            if (!success) {
                revert VAITransferFail();
            }
        }

        VAI.burn(msg.sender, stableTknAmountUSD);
        IERC20Upgradeable(STABLE_TOKEN_ADDRESS).safeTransfer(receiver, stableTknAmount);
        emit VAIForStableSwapped(stableTknAmountUSD, stableTknAmount, fee);
        return stableTknAmountUSD;
    }

    /**
     * @notice Swaps stable tokens for VAI with fees.
     * @dev This function adds support to fee-on-transfer tokens. The actualTransferAmt is calculated, by recording token balance state before and after the transfer.
     * @param receiver The address that will receive the VAI tokens.
     * @param stableTknAmount The amount of stable tokens to be swapped.
     * @return Amount of VAI minted to the sender.
     */
    // @custom:event Emits StableForVAISwapped event.
    function swapStableForVAI(
        address receiver,
        uint256 stableTknAmount
    ) external isActive nonReentrant returns (uint256) {
        _ensureNonzeroAddress(receiver);
        _ensureNonzeroAmount(stableTknAmount);
        // transfer IN, supporting fee-on-transfer tokens
        uint256 balanceBefore = IERC20Upgradeable(STABLE_TOKEN_ADDRESS).balanceOf(address(this));
        IERC20Upgradeable(STABLE_TOKEN_ADDRESS).safeTransferFrom(msg.sender, address(this), stableTknAmount);
        uint256 balanceAfter = IERC20Upgradeable(STABLE_TOKEN_ADDRESS).balanceOf(address(this));

        //calculate actual transfered amount (in case of fee-on-transfer tokens)
        uint256 actualTransferAmt = balanceAfter - balanceBefore;

        // update oracle price and calculate USD value of the stable token amount scaled in 18 decimals
        oracle.updateAssetPrice(STABLE_TOKEN_ADDRESS);
        uint256 actualTransferAmtInUSD = _previewTokenUSDAmount(actualTransferAmt, FeeDirection.IN);

        //calculate feeIn
        uint256 fee = _calculateFee(actualTransferAmtInUSD, FeeDirection.IN);
        uint256 vaiToMint = actualTransferAmtInUSD - fee;

        if (vaiMinted + actualTransferAmtInUSD > vaiMintCap) {
            revert VAIMintCapReached();
        }
        unchecked {
            vaiMinted += actualTransferAmtInUSD;
        }

        // mint VAI to receiver
        VAI.mint(receiver, vaiToMint);

        // mint VAI fee to venus treasury
        if (fee != 0) {
            VAI.mint(venusTreasury, fee);
        }

        emit StableForVAISwapped(actualTransferAmt, vaiToMint, fee);
        return vaiToMint;
    }

    /*** Admin Functions ***/

    /**
     * @notice Pause the PSM contract.
     * @dev Reverts if the contract is already paused.
     */
    // @custom:event Emits PSMPaused event.
    function pause() external {
        _checkAccessAllowed("pause()");
        if (isPaused) {
            revert AlreadyPaused();
        }
        isPaused = true;
        emit PSMPaused(msg.sender);
    }

    /**
     * @notice Resume the PSM contract.
     * @dev Reverts if the contract is not paused.
     */
    // @custom:event Emits PSMResumed event.
    function resume() external {
        _checkAccessAllowed("resume()");
        if (!isPaused) {
            revert NotPaused();
        }
        isPaused = false;
        emit PSMResumed(msg.sender);
    }

    /**
     * @notice Set the fee percentage for incoming swaps.
     * @dev Reverts if the new fee percentage is invalid (greater than or equal to BASIS_POINTS_DIVISOR).
     * @param feeIn_ The new fee percentage for incoming swaps.
     */
    // @custom:event Emits FeeInChanged event.
    function setFeeIn(uint256 feeIn_) external {
        _checkAccessAllowed("setFeeIn(uint256)");
        // feeIn = 10000 = 100%
        if (feeIn_ >= BASIS_POINTS_DIVISOR) {
            revert InvalidFee();
        }
        uint256 oldFeeIn = feeIn;
        feeIn = feeIn_;
        emit FeeInChanged(oldFeeIn, feeIn_);
    }

    /**
     * @notice Set the fee percentage for outgoing swaps.
     * @dev Reverts if the new fee percentage is invalid (greater than or equal to BASIS_POINTS_DIVISOR).
     * @param feeOut_ The new fee percentage for outgoing swaps.
     */
    // @custom:event Emits FeeOutChanged event.
    function setFeeOut(uint256 feeOut_) external {
        _checkAccessAllowed("setFeeOut(uint256)");
        // feeOut = 10000 = 100%
        if (feeOut_ >= BASIS_POINTS_DIVISOR) {
            revert InvalidFee();
        }
        uint256 oldFeeOut = feeOut;
        feeOut = feeOut_;
        emit FeeOutChanged(oldFeeOut, feeOut_);
    }

    /**
     * @dev Set the maximum amount of VAI that can be minted through this contract.
     * @param vaiMintCap_ The new maximum amount of VAI that can be minted.
     */
    // @custom:event Emits VAIMintCapChanged event.
    function setVAIMintCap(uint256 vaiMintCap_) external {
        _checkAccessAllowed("setVAIMintCap(uint256)");
        uint256 oldVAIMintCap = vaiMintCap;
        vaiMintCap = vaiMintCap_;
        emit VAIMintCapChanged(oldVAIMintCap, vaiMintCap_);
    }

    /**
     * @notice Set the address of the Venus Treasury contract.
     * @dev Reverts if the new address is zero.
     * @param venusTreasury_ The new address of the Venus Treasury contract.
     */
    // @custom:event Emits VenusTreasuryChanged event.
    function setVenusTreasury(address venusTreasury_) external {
        _checkAccessAllowed("setVenusTreasury(address)");
        _ensureNonzeroAddress(venusTreasury_);
        address oldTreasuryAddress = venusTreasury;
        venusTreasury = venusTreasury_;
        emit VenusTreasuryChanged(oldTreasuryAddress, venusTreasury_);
    }

    /**
     * @notice Set the address of the ResilientOracle contract.
     * @dev Reverts if the new address is zero.
     * @param oracleAddress_ The new address of the ResilientOracle contract.
     */
    // @custom:event Emits OracleChanged event.
    function setOracle(address oracleAddress_) external {
        _checkAccessAllowed("setOracle(address)");
        _ensureNonzeroAddress(oracleAddress_);
        address oldOracleAddress = address(oracle);
        oracle = ResilientOracleInterface(oracleAddress_);
        emit OracleChanged(oldOracleAddress, oracleAddress_);
    }

    /**
     * @dev Disabling renounceOwnership function.
     */
    function renounceOwnership() public override {}

    /*** Helper Functions ***/

    /**
     * @notice Calculates the amount of VAI that would be burnt from the user.
     * @dev This calculation might be off with a bit, if the price of the oracle for this asset is not updated in the block this function is invoked.
     * @param stableTknAmount The amount of stable tokens to be received after the swap.
     * @return The amount of VAI that would be taken from the user.
     */
    function previewSwapVAIForStable(uint256 stableTknAmount) external view returns (uint256) {
        _ensureNonzeroAmount(stableTknAmount);
        uint256 stableTknAmountUSD = _previewTokenUSDAmount(stableTknAmount, FeeDirection.OUT);
        uint256 fee = _calculateFee(stableTknAmountUSD, FeeDirection.OUT);

        if (vaiMinted < stableTknAmountUSD) {
            revert VAIMintedUnderflow();
        }

        return stableTknAmountUSD + fee;
    }

    /**
     * @notice Calculates the amount of VAI that would be sent to the receiver.
     * @dev This calculation might be off with a bit, if the price of the oracle for this asset is not updated in the block this function is invoked.
     * @param stableTknAmount The amount of stable tokens provided for the swap.
     * @return The amount of VAI that would be sent to the receiver.
     */
    function previewSwapStableForVAI(uint256 stableTknAmount) external view returns (uint256) {
        _ensureNonzeroAmount(stableTknAmount);
        uint256 stableTknAmountUSD = _previewTokenUSDAmount(stableTknAmount, FeeDirection.IN);

        //calculate feeIn
        uint256 fee = _calculateFee(stableTknAmountUSD, FeeDirection.IN);
        uint256 vaiToMint = stableTknAmountUSD - fee;

        if (vaiMinted + stableTknAmountUSD > vaiMintCap) {
            revert VAIMintCapReached();
        }

        return vaiToMint;
    }

    /**
     * @dev Calculates the USD value of the given amount of stable tokens depending on the swap direction.
     * @param amount The amount of stable tokens.
     * @param direction The direction of the swap.
     * @return The USD value of the given amount of stable tokens scaled by 1e18 taking into account the direction of the swap
     */
    function _previewTokenUSDAmount(uint256 amount, FeeDirection direction) internal view returns (uint256) {
        return (amount * _getPriceInUSD(direction)) / MANTISSA_ONE;
    }

    /**
     * @notice Get the price of stable token in USD.
     * @dev This function returns either min(1$,oraclePrice) or max(1$,oraclePrice) with a decimal scale (36 - asset_decimals). E.g. for 8 decimal token 1$ will be 1e28.
     * @param direction The direction of the swap: FeeDirection.IN or FeeDirection.OUT.
     * @return The price in USD, adjusted based on the selected direction.
     */
    function _getPriceInUSD(FeeDirection direction) internal view returns (uint256) {
        // get price with a scale = (36 - asset_decimals)
        uint256 price = oracle.getPrice(STABLE_TOKEN_ADDRESS);

        if (direction == FeeDirection.IN) {
            // MIN(1, price)
            return price < ONE_DOLLAR ? price : ONE_DOLLAR;
        } else {
            // MAX(1, price)
            return price > ONE_DOLLAR ? price : ONE_DOLLAR;
        }
    }

    /**
     * @notice Calculate the fee amount based on the input amount and fee percentage.
     * @dev Reverts if the fee percentage calculation results in rounding down to 0.
     * @param amount The input amount to calculate the fee from.
     * @param direction The direction of the fee: FeeDirection.IN or FeeDirection.OUT.
     * @return The fee amount.
     */
    function _calculateFee(uint256 amount, FeeDirection direction) internal view returns (uint256) {
        uint256 feePercent;
        if (direction == FeeDirection.IN) {
            feePercent = feeIn;
        } else {
            feePercent = feeOut;
        }
        if (feePercent == 0) {
            return 0;
        } else {
            // checking if the percent calculation will result in rounding down to 0
            if (amount * feePercent < BASIS_POINTS_DIVISOR) {
                revert AmountTooSmall();
            }
            return (amount * feePercent) / BASIS_POINTS_DIVISOR;
        }
    }

    /**
     * @notice Checks that the address is not the zero address.
     * @param someone The address to check.
     */
    function _ensureNonzeroAddress(address someone) private pure {
        if (someone == address(0)) revert ZeroAddress();
    }

    /**
     * @notice Checks that the amount passed as stable tokens is bigger than zero
     * @param amount The amount to validate
     */
    function _ensureNonzeroAmount(uint256 amount) private pure {
        if (amount == 0) revert ZeroAmount();
    }
}

