pragma solidity 0.8.25;

import { ResilientOracleInterface } from "@venusprotocol/oracle/contracts/interfaces/OracleInterface.sol";

import { VToken } from "../Tokens/VTokens/VToken.sol";
import { VAIControllerInterface } from "../Tokens/VAI/VAIControllerInterface.sol";
import { LiquidationManager } from "../LiquidationManager.sol";
import { ComptrollerLensInterface } from "./ComptrollerLensInterface.sol";
import { WeightFunction } from "./Diamond/interfaces/IFacetBase.sol";

enum Action {
    MINT,
    REDEEM,
    BORROW,
    REPAY,
    SEIZE,
    LIQUIDATE,
    TRANSFER,
    ENTER_MARKET,
    EXIT_MARKET
}

interface ComptrollerInterface {
    /// @notice Indicator that this is a Comptroller contract (for inspection)
    function isComptroller() external pure returns (bool);

    /*** Assets You Are In ***/

    function enterMarkets(address[] calldata vTokens) external returns (uint[] memory);

    function exitMarket(address vToken) external returns (uint);

    /*** Policy Hooks ***/

    function mintAllowed(address vToken, address minter, uint mintAmount) external returns (uint);

    function mintVerify(address vToken, address minter, uint mintAmount, uint mintTokens) external;

    function redeemAllowed(address vToken, address redeemer, uint redeemTokens) external returns (uint);

    function redeemVerify(address vToken, address redeemer, uint redeemAmount, uint redeemTokens) external;

    function borrowAllowed(address vToken, address borrower, uint borrowAmount) external returns (uint);

    function borrowVerify(address vToken, address borrower, uint borrowAmount) external;

    function executeFlashLoan(
        address payable initiator,
        address payable receiver,
        VToken[] calldata assets,
        uint256[] calldata amounts,
        uint256[] calldata modes,
        address onBehalfOf,
        bytes calldata param
    ) external;

    function repayBorrowAllowed(
        address vToken,
        address payer,
        address borrower,
        uint repayAmount
    ) external returns (uint);

    function repayBorrowVerify(
        address vToken,
        address payer,
        address borrower,
        uint repayAmount,
        uint borrowerIndex
    ) external;

    function liquidateBorrowAllowed(
        address vTokenBorrowed,
        address vTokenCollateral,
        address liquidator,
        address borrower,
        uint repayAmount
    ) external returns (uint);

    function liquidateBorrowAllowed(
        address vTokenBorrowed,
        address vTokenCollateral,
        address liquidator,
        address borrower,
        uint repayAmount,
        ComptrollerLensInterface.AccountSnapshot memory snapshot
    ) external returns (uint);

    function liquidateBorrowVerify(
        address vTokenBorrowed,
        address vTokenCollateral,
        address liquidator,
        address borrower,
        uint repayAmount,
        uint seizeTokens
    ) external;

    function seizeAllowed(
        address vTokenCollateral,
        address vTokenBorrowed,
        address liquidator,
        address borrower,
        uint seizeTokens
    ) external returns (uint);

    function seizeVerify(
        address vTokenCollateral,
        address vTokenBorrowed,
        address liquidator,
        address borrower,
        uint seizeTokens
    ) external;

    function transferAllowed(address vToken, address src, address dst, uint transferTokens) external returns (uint);

    function transferVerify(address vToken, address src, address dst, uint transferTokens) external;

    /*** Liquidity/Liquidation Calculations ***/

    function liquidateCalculateSeizeTokens(
        address borrower,
        address vTokenBorrowed,
        address vTokenCollateral,
        uint repayAmount
    ) external view returns (uint, uint);

    function liquidateCalculateSeizeTokens(
        address vTokenBorrowed,
        address vTokenCollateral,
        uint repayAmount,
        uint liquidationIncentiveMantissa
    ) external view returns (uint, uint);

    function setMintedVAIOf(address owner, uint amount) external returns (uint);

    function liquidateVAICalculateSeizeTokens(
        address vTokenCollateral,
        uint repayAmount,
        uint liquidationIncentiveMantissa
    ) external view returns (uint, uint);

    function liquidateVAICalculateSeizeTokens(
        address borrower,
        address vTokenCollateral,
        uint repayAmount
    ) external view returns (uint, uint);

    function getXVSAddress() external view returns (address);

    function markets(address) external view returns (bool, uint, bool, uint, uint);

    function oracle() external view returns (ResilientOracleInterface);

    function liquidationManager() external view returns (LiquidationManager);

    function getAccountLiquidity(address) external view returns (uint, uint, uint);

    function getAssetsIn(address) external view returns (VToken[] memory);

    function claimVenus(address) external;

    function venusAccrued(address) external view returns (uint);

    function venusSupplySpeeds(address) external view returns (uint);

    function venusBorrowSpeeds(address) external view returns (uint);

    function getAllMarkets() external view returns (VToken[] memory);

    function venusSupplierIndex(address, address) external view returns (uint);

    function venusInitialIndex() external view returns (uint224);

    function venusBorrowerIndex(address, address) external view returns (uint);

    function venusBorrowState(address) external view returns (uint224, uint32);

    function venusSupplyState(address) external view returns (uint224, uint32);

    function approvedDelegates(address borrower, address delegate) external view returns (bool);

    function vaiController() external view returns (VAIControllerInterface);

    function oldLiquidationIncentiveMantissa() external view returns (uint);

    function protocolPaused() external view returns (bool);

    function actionPaused(address market, Action action) external view returns (bool);

    function mintedVAIs(address user) external view returns (uint);

    function vaiMintRate() external view returns (uint);

    function getDynamicLiquidationIncentive(address borrower, address market) external view returns (uint256);

    function getDynamicLiquidationIncentive(
        address market,
        uint256 liquidationThresholdAvg,
        uint256 healthFactor
    ) external view returns (uint256);

    function getCollateralFactor(address vToken) external view returns (uint256);

    function getLiquidationThreshold(address vToken) external view returns (uint256);

    function getHypotheticalHealthSnapshot(
        address account,
        VToken vTokenModify,
        uint redeemTokens,
        uint borrowAmount
    ) external view returns (uint256, ComptrollerLensInterface.AccountSnapshot memory);

    function authorizedFlashLoan(address account) external view returns (bool);

    function delegateAuthorizationFlashloan(
        address account,
        address market,
        address delegate
    ) external view returns (bool);
    function userPoolId(address account) external view returns (uint96);

    function getLiquidationIncentive(address vToken) external view returns (uint256);

    function getEffectiveLiquidationIncentive(address account, address vToken) external view returns (uint256);

    function getEffectiveLtvFactor(
        address account,
        address vToken,
        WeightFunction weightingStrategy
    ) external view returns (uint256);

    function lastPoolId() external view returns (uint96);

    function pools(uint96 poolId) external view returns (string memory label);

    function getPoolVTokens(uint96 poolId) external view returns (address[] memory);

    function poolMarkets(
        uint96 poolId,
        address vToken
    )
        external
        view
        returns (
            bool isListed,
            uint256 collateralFactorMantissa,
            bool isVenus,
            uint256 liquidationThresholdMantissa,
            uint256 maxLiquidationIncentiveMantissa,
            uint96 marketPoolId,
            bool isBorrowAllowed
        );
}

interface IVAIVault {
    function updatePendingRewards() external;
}

interface IComptroller {
    function liquidationIncentiveMantissa() external view returns (uint);

    /*** Treasury Data ***/
    function treasuryAddress() external view returns (address);

    function treasuryPercent() external view returns (uint);
}
","keccak256":"0xe8b22068aee7b4d111e6b6abd07b5dc0f873cc57d23b8f389e93a746abece9e7","license":"BSD-3-Clause"},"contracts/Comptroller/ComptrollerLensInterface.sol":{"content":"// SPDX-License-Identifier: BSD-3-Clause

pragma solidity 0.8.25;

import { VToken } from "../Tokens/VTokens/VToken.sol";
import { WeightFunction } from "./Diamond/interfaces/IFacetBase.sol";

interface ComptrollerLensInterface {
    struct AccountSnapshot {
        // Total collateral value supplied by the account (USD, scaled by 1e18)
        uint256 totalCollateral;
        // Collateral value weighted by each asset's liquidation threshold or collateral factor (USD, scaled by 1e18)
        uint256 weightedCollateral;
        // Total borrowed value by the account (USD, scaled by 1e18)
        uint256 borrows;
        // Amount of excess collateral available for borrowing (USD, scaled by 1e18)
        uint256 liquidity;
        // Amount by which the account is undercollateralized (USD, scaled by 1e18)
        uint256 shortfall;
        // Average liquidation threshold across all supplied assets (scaled by 1e18)
        uint256 liquidationThresholdAvg;
        // Health factor of the account, used to assess liquidation risk (scaled by 1e18)
        uint256 healthFactor;
        // Dynamic liquidation incentive factor applied during liquidations (scaled by 1e18)
        uint256 dynamicLiquidationIncentiveMantissa;
    }

    // Just need to make borrower first then comptroller
    function liquidateCalculateSeizeTokens(
        address comptroller,
        address borrower,
        address vTokenBorrowed,
        address vTokenCollateral,
        uint256 actualRepayAmount
    ) external view returns (uint256, uint256);

    function liquidateCalculateSeizeTokens(
        address comptroller,
        address vTokenBorrowed,
        address vTokenCollateral,
        uint256 actualRepayAmount,
        uint256 liquidationIncentiveMantissa
    ) external view returns (uint256, uint256);

    function liquidateVAICalculateSeizeTokens(
        address comptroller,
        address vTokenCollateral,
        uint256 actualRepayAmount,
        uint256 liquidationIncentiveMantissa
    ) external view returns (uint256, uint256);

    function liquidateVAICalculateSeizeTokens(
        address comptroller,
        address borrower,
        address vTokenCollateral,
        uint256 actualRepayAmount
    ) external view returns (uint256, uint256);

    function getHypotheticalAccountLiquidity(
        address comptroller,
        address account,
        VToken vTokenModify,
        uint256 redeemTokens,
        uint256 borrowAmount,
        WeightFunction weightingStrategy
    ) external view returns (uint256, uint256, uint256);

    function getAccountHealthSnapshot(
        address comptroller,
        address account,
        VToken vTokenModify,
        uint256 redeemTokens,
        uint256 borrowAmount,
        WeightFunction weightingStrategy
    ) external view returns (uint256, AccountSnapshot memory);
}
","keccak256":"0x8cd7d089771480417651bd476a9071b899b4e239e1ddcc2d4eb0b324b99a0676","license":"BSD-3-Clause"},"contracts/Comptroller/Diamond/interfaces/IFacetBase.sol":{"content":"// SPDX-License-Identifier: BSD-3-Clause

pragma solidity 0.8.25;

import { Action } from "../../../Comptroller/ComptrollerInterface.sol";
import { PoolMarketId } from "../../../Comptroller/Types/PoolMarketId.sol";

import { VToken } from "../../../Tokens/VTokens/VToken.sol";
import { Action } from "../../../Comptroller/ComptrollerInterface.sol";
import { ComptrollerLensInterface } from "../../../Comptroller/ComptrollerLensInterface.sol";

enum WeightFunction {
    /// @notice Use the collateral factor of the asset for weighting
    USE_COLLATERAL_FACTOR,
    /// @notice Use the liquidation threshold of the asset for weighting
    USE_LIQUIDATION_THRESHOLD
}

interface IFacetBase {
    /**
     * @notice The initial XVS rewards index for a market
     */
    function venusInitialIndex() external pure returns (uint224);

    /**
     * @notice Checks if a certain action is paused on a market
     * @param action Action id
     * @param market vToken address
     */
    function actionPaused(address market, Action action) external view returns (bool);

    /**
     * @notice Returns the XVS address
     * @return The address of XVS token
     */
    function getXVSAddress() external view returns (address);

    function getHypotheticalHealthSnapshot(
        address account,
        VToken vTokenModify,
        uint256 redeemTokens,
        uint256 borrowAmount,
        WeightFunction weightingStrategy
    ) external view returns (uint256 err, ComptrollerLensInterface.AccountSnapshot memory snapshot);

    function getPoolMarketIndex(uint96 poolId, address vToken) external pure returns (PoolMarketId);

    function corePoolId() external pure returns (uint96);
}
