// SPDX-License-Identifier: BSD-3-Clause

pragma solidity 0.8.25;

import { VToken } from "../../../Tokens/VTokens/VToken.sol";
import { Action } from "../../ComptrollerInterface.sol";
import { IMarketFacet } from "../interfaces/IMarketFacet.sol";
import { FacetBase } from "./FacetBase.sol";
import { PoolMarketId } from "../../../Comptroller/Types/PoolMarketId.sol";
import { WeightFunction } from "../interfaces/IFacetBase.sol";

/**
 * @title MarketFacet
 * @author Venus
 * @dev This facet contains all the methods related to the market's management in the pool
 * @notice This facet contract contains functions regarding markets
 */
contract MarketFacet is IMarketFacet, FacetBase {
    /// @notice Emitted when an admin supports a market
    event MarketListed(VToken indexed vToken);

    /// @notice Emitted when an account exits a market
    event MarketExited(VToken indexed vToken, address indexed account);

    /// @notice Emitted when the borrowing or redeeming delegate rights are updated for an account
    event DelegateUpdated(address indexed approver, address indexed delegate, bool approved);

    /// @notice Emitted when an admin unlists a market
    event MarketUnlisted(address indexed vToken);

    /// @notice Emitted when a market is initialized in a pool
    event PoolMarketInitialized(uint96 indexed poolId, address indexed market);

    /// @notice Emitted when a user enters or exits a pool (poolId = 0 means exit)
    event PoolSelected(address indexed account, uint96 previousPoolId, uint96 indexed newPoolId);

    /// @notice Emitted when a vToken market is removed from a pool
    event PoolMarketRemoved(uint96 indexed poolId, address indexed vToken);

    /// @notice Emitted when a new pool is created
    event PoolCreated(uint96 indexed poolId, string label);

    /// @notice Indicator that this is a Comptroller contract (for inspection)
    function isComptroller() public pure returns (bool) {
        return true;
    }

    /**
     * @notice Returns the vToken markets an account has entered in the Core Pool
     * @dev Reads membership from the Core Pool (`poolId = 0`). Although the account may have entered other pools,
     *      all entered market state is recorded in the Core Pool indexes, making this function applicable to all poolIds
     * @param account The address of the account to query
     * @return assets A dynamic array of vToken markets the account has entered
     */
    function getAssetsIn(address account) external view returns (VToken[] memory) {
        uint256 len;
        VToken[] memory _accountAssets = accountAssets[account];
        uint256 _accountAssetsLength = _accountAssets.length;

        VToken[] memory assetsIn = new VToken[](_accountAssetsLength);

        for (uint256 i; i < _accountAssetsLength; ++i) {
            Market storage market = getCorePoolMarket(address(_accountAssets[i]));
            if (market.isListed) {
                assetsIn[len] = _accountAssets[i];
                ++len;
            }
        }

        assembly {
            mstore(assetsIn, len)
        }

        return assetsIn;
    }

    /**
     * @notice Return all of the markets
     * @dev The automatic getter may be used to access an individual market
     * @return The list of market addresses
     */
    function getAllMarkets() external view returns (VToken[] memory) {
        return allMarkets;
    }

    /**
     * @notice Calculate number of tokens of collateral asset to seize given an underlying amount
     * @dev Used in liquidation (called in vToken.liquidateBorrowFresh)
     * @param vTokenBorrowed The address of the borrowed vToken
     * @param vTokenCollateral The address of the collateral vToken
     * @param actualRepayAmount The amount of vTokenBorrowed underlying to convert into vTokenCollateral tokens
     * @return (errorCode, number of vTokenCollateral tokens to be seized in a liquidation)
     */
    function liquidateCalculateSeizeTokens(
        address vTokenBorrowed,
        address vTokenCollateral,
        uint256 actualRepayAmount
    ) external view returns (uint256, uint256) {
        (uint256 err, uint256 seizeTokens) = comptrollerLens.liquidateCalculateSeizeTokens(
            address(this),
            vTokenBorrowed,
            vTokenCollateral,
            actualRepayAmount
        );
        return (err, seizeTokens);
    }

    /**
     * @notice Calculate number of tokens of collateral asset to seize given an underlying amount
     * @dev Used in liquidation (called in vToken.liquidateBorrowFresh)
     * @param borrower Address of borrower whose collateral is being seized
     * @param vTokenBorrowed The address of the borrowed vToken
     * @param vTokenCollateral The address of the collateral vToken
     * @param actualRepayAmount The amount of vTokenBorrowed underlying to convert into vTokenCollateral tokens
     * @return (errorCode, number of vTokenCollateral tokens to be seized in a liquidation)
     */
    function liquidateCalculateSeizeTokens(
        address borrower,
        address vTokenBorrowed,
        address vTokenCollateral,
        uint256 actualRepayAmount
    ) external view returns (uint256, uint256) {
        (uint256 err, uint256 seizeTokens) = comptrollerLens.liquidateCalculateSeizeTokens(
            borrower,
            address(this),
            vTokenBorrowed,
            vTokenCollateral,
            actualRepayAmount
        );
        return (err, seizeTokens);
    }

    /**
     * @notice Calculate number of tokens of collateral asset to seize given an underlying amount
     * @dev Used in liquidation (called in vToken.liquidateBorrowFresh)
     * @param vTokenCollateral The address of the collateral vToken
     * @param actualRepayAmount The amount of vTokenBorrowed underlying to convert into vTokenCollateral tokens
     * @return (errorCode, number of vTokenCollateral tokens to be seized in a liquidation)
     */
    function liquidateVAICalculateSeizeTokens(
        address vTokenCollateral,
        uint256 actualRepayAmount
    ) external view returns (uint256, uint256) {
        (uint256 err, uint256 seizeTokens) = comptrollerLens.liquidateVAICalculateSeizeTokens(
            address(this),
            vTokenCollateral,
            actualRepayAmount
        );
        return (err, seizeTokens);
    }

    /**
     * @notice Returns whether the given account has entered the specified vToken market in the Core Pool
     * @dev Reads membership from the Core Pool (`poolId = 0`). Although the account may have entered other pools,
     *      all entered market state is recorded in the Core Pool indexes, making this function applicable to all poolIds
     * @param account The address of the account to check
     * @param vToken The vToken to check
     * @return True if the account is in the asset, otherwise false
     */
    function checkMembership(address account, VToken vToken) external view returns (bool) {
        return getCorePoolMarket(address(vToken)).accountMembership[account];
    }

    /**
     * @notice Checks whether the given vToken market is listed in the Core Pool (`poolId = 0`)
     * @param vToken The vToken Address of the market to check
     * @return listed True if the (Core Pool, vToken) market is listed, otherwise false
     */
    function isMarketListed(VToken vToken) external view returns (bool) {
        return getCorePoolMarket(address(vToken)).isListed;
    }

    /**
     * @notice Add assets to be included in account liquidity calculation
     * @param vTokens The list of addresses of the vToken markets to be enabled
     * @return Success indicator for whether each corresponding market was entered
     */
    function enterMarkets(address[] calldata vTokens) external returns (uint256[] memory) {
        uint256 len = vTokens.length;

        uint256[] memory results = new uint256[](len);
        for (uint256 i; i < len; ++i) {
            results[i] = uint256(addToMarketInternal(VToken(vTokens[i]), msg.sender));
        }

        return results;
    }

    /**
     * @notice Unlists the given vToken market from the Core Pool (`poolId = 0`) by setting `isListed` to false
     * @dev Checks if market actions are paused and borrowCap/supplyCap/CF are set to 0
     * @param market The address of the market (vToken) to unlist
     * @return uint256 0=success, otherwise a failure (See enum Error for details)
     */
    function unlistMarket(address market) external returns (uint256) {
        ensureAllowed("unlistMarket(address)");

        Market storage _market = getCorePoolMarket(market);

        if (!_market.isListed) {
            return fail(Error.MARKET_NOT_LISTED, FailureInfo.UNLIST_MARKET_NOT_LISTED);
        }

        require(actionPaused(market, Action.BORROW), "borrow action is not paused");
        require(actionPaused(market, Action.MINT), "mint action is not paused");
        require(actionPaused(market, Action.REDEEM), "redeem action is not paused");
        require(actionPaused(market, Action.REPAY), "repay action is not paused");
        require(actionPaused(market, Action.ENTER_MARKET), "enter market action is not paused");
        require(actionPaused(market, Action.LIQUIDATE), "liquidate action is not paused");
        require(actionPaused(market, Action.SEIZE), "seize action is not paused");
        require(actionPaused(market, Action.TRANSFER), "transfer action is not paused");
        require(actionPaused(market, Action.EXIT_MARKET), "exit market action is not paused");

        require(borrowCaps[market] == 0, "borrow cap is not 0");
        require(supplyCaps[market] == 0, "supply cap is not 0");

        require(_market.collateralFactorMantissa == 0, "collateral factor is not 0");

        _market.isListed = false;
        emit MarketUnlisted(market);

        return uint256(Error.NO_ERROR);
    }

    /**
     * @notice Removes asset from sender's account liquidity calculation
     * @dev Sender must not have an outstanding borrow balance in the asset,
     *  or be providing necessary collateral for an outstanding borrow
     * @param vTokenAddress The address of the asset to be removed
     * @return Whether or not the account successfully exited the market
     */
    function exitMarket(address vTokenAddress) external returns (uint256) {
        checkActionPauseState(vTokenAddress, Action.EXIT_MARKET);

        VToken vToken = VToken(vTokenAddress);
        /* Get sender tokensHeld and amountOwed underlying from the vToken */
        (uint256 oErr, uint256 tokensHeld, uint256 amountOwed, ) = vToken.getAccountSnapshot(msg.sender);
        require(oErr == 0, "getAccountSnapshot failed"); // semi-opaque error code

        /* Fail if the sender has a borrow balance */
        if (amountOwed != 0) {
            return fail(Error.NONZERO_BORROW_BALANCE, FailureInfo.EXIT_MARKET_BALANCE_OWED);
        }

        /* Fail if the sender is not permitted to redeem all of their tokens */
        uint256 allowed = redeemAllowedInternal(vTokenAddress, msg.sender, tokensHeld);
        if (allowed != 0) {
            return failOpaque(Error.REJECTION, FailureInfo.EXIT_MARKET_REJECTION, allowed);
        }

        Market storage marketToExit = getCorePoolMarket(address(vToken));

        /* Return true if the sender is not already in the market */
        if (!marketToExit.accountMembership[msg.sender]) {
            return uint256(Error.NO_ERROR);
        }

        /* Set vToken account membership to false */
        delete marketToExit.accountMembership[msg.sender];

        /* Delete vToken from the accounts list of assets */
        // In order to delete vToken, copy last item in list to location of item to be removed, reduce length by 1
        VToken[] storage userAssetList = accountAssets[msg.sender];
        uint256 len = userAssetList.length;
        uint256 i;
        for (; i < len; ++i) {
            if (userAssetList[i] == vToken) {
                userAssetList[i] = userAssetList[len - 1];
                userAssetList.pop();
                break;
            }
        }

        // We *must* have found the asset in the list or our redundant data structure is broken
        assert(i < len);

        emit MarketExited(vToken, msg.sender);

        return uint256(Error.NO_ERROR);
    }

    /**
     * @notice Alias to _supportMarket to support the Isolated Lending Comptroller Interface
     * @param vToken The address of the market (token) to list
     * @return uint256 0=success, otherwise a failure. (See enum Error for details)
     */
    function supportMarket(VToken vToken) external returns (uint256) {
        return __supportMarket(vToken);
    }

    /**
     * @notice Adds the given vToken market to the Core Pool (`poolId = 0`) and marks it as listed
     * @dev Allows a privileged role to add and list markets to the Comptroller
     * @param vToken The address of the vToken market to list in the Core Pool
     * @return uint256 0=success, otherwise a failure. (See enum Error for details)
     */
    function _supportMarket(VToken vToken) external returns (uint256) {
        return __supportMarket(vToken);
    }

    /**
     * @notice Grants or revokes the borrowing or redeeming delegate rights to / from an account
     *  If allowed, the delegate will be able to borrow funds on behalf of the sender
     *  Upon a delegated borrow, the delegate will receive the funds, and the borrower
     *  will see the debt on their account
     *  Upon a delegated redeem, the delegate will receive the redeemed amount and the approver
     *  will see a deduction in his vToken balance
     * @param delegate The address to update the rights for
     * @param approved Whether to grant (true) or revoke (false) the borrowing or redeeming rights
     */
    function updateDelegate(address delegate, bool approved) external {
        ensureNonzeroAddress(delegate);
        require(approvedDelegates[msg.sender][delegate] != approved, "Delegation status unchanged");

        _updateDelegate(msg.sender, delegate, approved);
    }

    /**
     * @notice Allows a user to switch to a new pool (e.g., e-mode ).
     * @param poolId The ID of the pool the user wants to enter.
     * @custom:error PoolDoesNotExist The specified pool ID does not exist.
     * @custom:error AlreadyInSelectedPool The user is already in the target pool.
     * @custom:error IncompatibleBorrowedAssets The user's current borrows are incompatible with the new pool.
     * @custom:error LiquidityCheckFailed The user's liquidity is insufficient after switching pools.
     * @custom:error InactivePool The user is trying to enter inactive pool.
     * @custom:event PoolSelected Emitted after a successful pool switch.
     */
    function enterPool(uint96 poolId) external {
        if (poolId > lastPoolId) revert PoolDoesNotExist(poolId);

        if (poolId == userPoolId[msg.sender]) {
            revert AlreadyInSelectedPool();
        }

        if (poolId != corePoolId && !pools[poolId].isActive) {
            revert InactivePool(poolId);
        }

        if (!hasValidPoolBorrows(msg.sender, poolId)) {
            revert IncompatibleBorrowedAssets();
        }

        emit PoolSelected(msg.sender, userPoolId[msg.sender], poolId);

        userPoolId[msg.sender] = poolId;

        (uint256 error, , uint256 shortfall) = _getAccountLiquidity(msg.sender, WeightFunction.USE_COLLATERAL_FACTOR);

        if (error != 0 || shortfall > 0) {
            revert LiquidityCheckFailed(error, shortfall);
        }
    }

    /**
     * @notice Creates a new pool with the given label.
     * @param label name for the pool (must be non-empty).
     * @return poolId The incremental unique identifier of the newly created pool.
     * @custom:error EmptyPoolLabel Reverts if the provided label is an empty string.
     * @custom:event PoolCreated Emitted after successfully creating a new pool.
     */
    function createPool(string memory label) external returns (uint96) {
        ensureAllowed("createPool(string)");

        if (bytes(label).length == 0) {
            revert EmptyPoolLabel();
        }

        uint96 poolId = ++lastPoolId;
        PoolData storage newPool = pools[poolId];
        newPool.label = label;
        newPool.isActive = true;

        emit PoolCreated(poolId, label);
        return poolId;
    }

    /**
     * @notice Batch initializes market entries with basic config.
     * @param poolIds Array of pool IDs.
     * @param vTokens Array of market (vToken) addresses.
     * @custom:error ArrayLengthMismatch Reverts if `poolIds` and `vTokens` arrays have different lengths or if the length is zero.
     * @custom:error InvalidOperationForCorePool Reverts when attempting to call pool-specific methods on the Core Pool.
     * @custom:error PoolDoesNotExist Reverts if the target pool ID does not exist.
     * @custom:error MarketNotListedInCorePool Reverts if the market is not listed in the core pool.
     * @custom:error MarketAlreadyListed Reverts if the given market is already listed in the specified pool.
     * @custom:error InactivePool Reverts if attempted to add markets to an inactive pool.
     * @custom:event PoolMarketInitialized Emitted after successfully initializing a market in a pool.
     */
    function addPoolMarkets(uint96[] calldata poolIds, address[] calldata vTokens) external {
        ensureAllowed("addPoolMarkets(uint96[],address[])");

        uint256 len = poolIds.length;
        if (len == 0 || len != vTokens.length) {
            revert ArrayLengthMismatch();
        }

        for (uint256 i; i < len; ++i) {
            _addPoolMarket(poolIds[i], vTokens[i]);
        }
    }

    /**
     * @notice Removes a market (vToken) from the specified pool.
     * @param poolId The ID of the pool from which the market should be removed.
     * @param vToken The address of the market token to remove.
     * @custom:error InvalidOperationForCorePool Reverts if called on the Core Pool.
     * @custom:error PoolMarketNotFound Reverts if the market is not listed in the pool.
     * @custom:event PoolMarketRemoved Emitted after a market is successfully removed from a pool.
     */
    function removePoolMarket(uint96 poolId, address vToken) external {
        ensureAllowed("removePoolMarket(uint96,address)");

        if (poolId == corePoolId) revert InvalidOperationForCorePool();
        PoolMarketId index = getPoolMarketIndex(poolId, vToken);
        if (!_poolMarkets[index].isListed) {
            revert PoolMarketNotFound(poolId, vToken);
        }

        address[] storage assets = pools[poolId].vTokens;

        uint256 length = assets.length;
        for (uint256 i; i < length; ++i) {
            if (assets[i] == vToken) {
                assets[i] = assets[length - 1];
                assets.pop();
                break;
            }
        }

        delete _poolMarkets[index];

        emit PoolMarketRemoved(poolId, vToken);
    }

    /**
     * @notice Get the core pool collateral factor for a vToken
     * @param vToken The address of the vToken to get the collateral factor for
     * @return The collateral factor for the vToken, scaled by 1e18
     */
    function getCollateralFactor(address vToken) external view returns (uint256) {
        (uint256 cf, , ) = getLiquidationParams(corePoolId, vToken);
        return cf;
    }

    /**
     * @notice Get the core pool liquidation threshold for a vToken
     * @param vToken The address of the vToken to get the liquidation threshold for
     * @return The liquidation threshold for the vToken, scaled by 1e18
     */
    function getLiquidationThreshold(address vToken) external view returns (uint256) {
        (, uint256 lt, ) = getLiquidationParams(corePoolId, vToken);
        return lt;
    }

    /**
     * @notice Get the core pool liquidation Incentive for a vToken
     * @param vToken The address of the vToken to get the liquidation Incentive for
     * @return liquidationIncentive The liquidation incentive for the vToken, scaled by 1e18
     */
    function getLiquidationIncentive(address vToken) external view returns (uint256) {
        (, , uint256 li) = getLiquidationParams(corePoolId, vToken);
        return li;
    }

    /**
     * @notice Get the effective loan-to-value factor (collateral factor or liquidation threshold) for a given account and market.
     * @dev The value is determined by the pool entered by the account and the specified vToken via
     *      `getLiquidationParams()`. If the pool is inactive, or if the vToken is not configured in the
     *      account's pool and `allowCorePoolFallback` is enabled, the core pool (poolId = 0) values are used.
     *      This value is used for account liquidity calculations and liquidation checks.
     * @param account The account whose pool is used to determine the market's risk parameters.
     * @param vToken The address of the vToken market.
     * @param weightingStrategy The weighting strategy to use:
     *                          - `WeightFunction.USE_COLLATERAL_FACTOR` to use collateral factor
     *                          - `WeightFunction.USE_LIQUIDATION_THRESHOLD` to use liquidation threshold
     * @return factor The effective loan-to-value factor, scaled by 1e18.
     */
    function getEffectiveLtvFactor(
        address account,
        address vToken,
        WeightFunction weightingStrategy
    ) external view returns (uint256) {
        (uint256 cf, uint256 lt, ) = getLiquidationParams(userPoolId[account], vToken);
        if (weightingStrategy == WeightFunction.USE_COLLATERAL_FACTOR) return cf;
        else if (weightingStrategy == WeightFunction.USE_LIQUIDATION_THRESHOLD) return lt;
        else revert InvalidWeightingStrategy(weightingStrategy);
    }

    /**
     * @notice Get the Effective Liquidation Incentive for a given account and market
     * @dev The incentive is determined by the pool entered by the account and the specified vToken via
     *      `getLiquidationParams()`. If the pool is inactive, or if the vToken is not configured in the
     *      account's pool and `allowCorePoolFallback` is enabled, the core pool (poolId = 0) values are used
     * @param account The account whose pool is used to determine the market's risk parameters
     * @param vToken The address of the vToken market
     * @return The liquidation Incentive for the vToken, scaled by 1e18
     */
    function getEffectiveLiquidationIncentive(address account, address vToken) external view returns (uint256) {
        (, , uint256 li) = getLiquidationParams(userPoolId[account], vToken);
        return li;
    }

    /**
     * @notice Returns the full list of vTokens for a given pool ID.
     * @param poolId The ID of the pool whose vTokens are being queried.
     * @return An array of vToken addresses associated with the pool.
     * @custom:error PoolDoesNotExist Reverts if the given pool ID do not exist.
     * @custom:error InvalidOperationForCorePool Reverts if called on the Core Pool.
     */
    function getPoolVTokens(uint96 poolId) external view returns (address[] memory) {
        if (poolId > lastPoolId) revert PoolDoesNotExist(poolId);
        if (poolId == corePoolId) revert InvalidOperationForCorePool();
        return pools[poolId].vTokens;
    }

    /**
     * @notice Returns the market configuration for a vToken in the core pool (poolId = 0).
     * @dev Fetches the Market struct associated with the core pool and returns all relevant parameters.
     * @param vToken The address of the vToken whose market configuration is to be fetched.
     * @return isListed Whether the market is listed and enabled.
     * @return collateralFactorMantissa The maximum borrowable percentage of collateral, in mantissa.
     * @return isVenus Whether this market is eligible for VENUS rewards.
     * @return liquidationThresholdMantissa The threshold at which liquidation is triggered, in mantissa.
     * @return liquidationIncentiveMantissa The max liquidation incentive allowed for this market, in mantissa.
     * @return marketPoolId The pool ID this market belongs to.
     * @return isBorrowAllowed Whether borrowing is allowed in this market.
     */
    function markets(
        address vToken
    )
        external
        view
        returns (
            bool isListed,
            uint256 collateralFactorMantissa,
            bool isVenus,
            uint256 liquidationThresholdMantissa,
            uint256 liquidationIncentiveMantissa,
            uint96 marketPoolId,
            bool isBorrowAllowed
        )
    {
        return poolMarkets(corePoolId, vToken);
    }

    /**
     * @notice Returns the market configuration for a vToken from _poolMarkets.
     * @dev Fetches the Market struct associated with the poolId and returns all relevant parameters.
     * @param poolId The ID of the pool whose market configuration is being queried.
     * @param vToken The address of the vToken whose market configuration is to be fetched.
     * @return isListed Whether the market is listed and enabled.
     * @return collateralFactorMantissa The maximum borrowable percentage of collateral, in mantissa.
     * @return isVenus Whether this market is eligible for XVS rewards.
     * @return liquidationThresholdMantissa The threshold at which liquidation is triggered, in mantissa.
     * @return liquidationIncentiveMantissa The liquidation incentive allowed for this market, in mantissa.
     * @return marketPoolId The pool ID this market belongs to.
     * @return isBorrowAllowed Whether borrowing is allowed in this market.
     * @custom:error PoolDoesNotExist Reverts if the given pool ID do not exist.
     */
    function poolMarkets(
        uint96 poolId,
        address vToken
    )
        public
        view
        returns (
            bool isListed,
            uint256 collateralFactorMantissa,
            bool isVenus,
            uint256 liquidationThresholdMantissa,
            uint256 liquidationIncentiveMantissa,
            uint96 marketPoolId,
            bool isBorrowAllowed
        )
    {
        if (poolId > lastPoolId) revert PoolDoesNotExist(poolId);
        PoolMarketId key = getPoolMarketIndex(poolId, vToken);
        Market storage m = _poolMarkets[key];

        return (
            m.isListed,
            m.collateralFactorMantissa,
            m.isVenus,
            m.liquidationThresholdMantissa,
            m.liquidationIncentiveMantissa,
            m.poolId,
            m.isBorrowAllowed
        );
    }

    /**
     * @notice Returns true if the user can switch to the given target pool, i.e.,
     * all markets they have borrowed from are also borrowable in the target pool.
     * @param account The address of the user attempting to switch pools.
     * @param targetPoolId The pool ID the user wants to switch into.
     * @return bool True if the switch is allowed, otherwise False.
     */
    function hasValidPoolBorrows(address account, uint96 targetPoolId) public view returns (bool) {
        VToken[] memory assets = accountAssets[account];
        if (targetPoolId != corePoolId && mintedVAIs[account] > 0) {
            return false;
        }

        for (uint256 i; i < assets.length; ++i) {
            VToken vToken = assets[i];
            PoolMarketId index = getPoolMarketIndex(targetPoolId, address(vToken));

            if (!_poolMarkets[index].isBorrowAllowed) {
                if (vToken.borrowBalanceStored(account) > 0) {
                    return false;
                }
            }
        }
        return true;
    }

    function _updateDelegate(address approver, address delegate, bool approved) internal {
        approvedDelegates[approver][delegate] = approved;
        emit DelegateUpdated(approver, delegate, approved);
    }

    function _addMarketInternal(VToken vToken) internal {
        uint256 allMarketsLength = allMarkets.length;
        for (uint256 i; i < allMarketsLength; ++i) {
            require(allMarkets[i] != vToken, "already added");
        }
        allMarkets.push(vToken);
    }

    function _initializeMarket(address vToken) internal {
        uint32 blockNumber = getBlockNumberAsUint32();

        VenusMarketState storage supplyState = venusSupplyState[vToken];
        VenusMarketState storage borrowState = venusBorrowState[vToken];

        /*
         * Update market state indices
         */
        if (supplyState.index == 0) {
            // Initialize supply state index with default value
            supplyState.index = venusInitialIndex;
        }

        if (borrowState.index == 0) {
            // Initialize borrow state index with default value
            borrowState.index = venusInitialIndex;
        }

        /*
         * Update market state block numbers
         */
        supplyState.block = borrowState.block = blockNumber;
    }

    function __supportMarket(VToken vToken) internal returns (uint256) {
        ensureAllowed("_supportMarket(address)");

        if (getCorePoolMarket(address(vToken)).isListed) {
            return fail(Error.MARKET_ALREADY_LISTED, FailureInfo.SUPPORT_MARKET_EXISTS);
        }

        vToken.isVToken(); // Sanity check to make sure its really a VToken

        // Note that isVenus is not in active use anymore
        Market storage newMarket = getCorePoolMarket(address(vToken));
        newMarket.isListed = true;
        newMarket.isVenus = false;
        newMarket.collateralFactorMantissa = 0;

        _addMarketInternal(vToken);
        _initializeMarket(address(vToken));

        emit MarketListed(vToken);

        return uint256(Error.NO_ERROR);
    }

    function _addPoolMarket(uint96 poolId, address vToken) internal {
        if (poolId == corePoolId) revert InvalidOperationForCorePool();
        if (poolId > lastPoolId) revert PoolDoesNotExist(poolId);
        if (!pools[poolId].isActive) revert InactivePool(poolId);

        // Core Pool Index
        PoolMarketId index = getPoolMarketIndex(corePoolId, vToken);
        if (!_poolMarkets[index].isListed) revert MarketNotListedInCorePool();

        // Pool Index
        index = getPoolMarketIndex(poolId, vToken);
        if (_poolMarkets[index].isListed) revert MarketAlreadyListed(poolId, vToken);

        Market storage m = _poolMarkets[index];
        m.poolId = poolId;
        m.isListed = true;

        pools[poolId].vTokens.push(vToken);

        emit PoolMarketInitialized(poolId, vToken);
    }

    /**
     * @notice Returns only the core risk parameters (CF, LI, LT) for a vToken in a specific pool.
     * @dev If the pool is inactive, or if the vToken is not configured in the given pool and
     *      `allowCorePoolFallback` is enabled, falls back to the core pool (poolId = 0) values.
     * @return collateralFactorMantissa The max borrowable percentage of collateral, in mantissa.
     * @return liquidationThresholdMantissa The threshold at which liquidation is triggered, in mantissa.
     * @return liquidationIncentiveMantissa The liquidation incentive allowed for this market, in mantissa.
     */
    function getLiquidationParams(
        uint96 poolId,
        address vToken
    )
        internal
        view
        returns (
            uint256 collateralFactorMantissa,
            uint256 liquidationThresholdMantissa,
            uint256 liquidationIncentiveMantissa
        )
    {
        PoolData storage pool = pools[poolId];
        Market storage market;

        if (poolId == corePoolId || !pool.isActive) {
            market = getCorePoolMarket(vToken);
        } else {
            PoolMarketId poolKey = getPoolMarketIndex(poolId, vToken);
            Market storage poolMarket = _poolMarkets[poolKey];
            market = (!poolMarket.isListed && pool.allowCorePoolFallback) ? getCorePoolMarket(vToken) : poolMarket;
        }

        return (
            market.collateralFactorMantissa,
            market.liquidationThresholdMantissa,
            market.liquidationIncentiveMantissa
        );
    }
}

contracts/Comptroller/Diamond/facets/Pol.content:
// SPDX-License-Identifier: BSD-3-Clause

pragma solidity 0.8.25;

import { VToken } from "../../../Tokens/VTokens/VToken.sol";
import { Action } from "../../ComptrollerInterface.sol";
import { IPolicyFacet } from "../interfaces/IPolicyFacet.sol";
import { XVSRewardsHelper } from "./XVSRewardsHelper.sol";
import { PoolMarketId } from "../../../Comptroller/Types/PoolMarketId.sol";
import { WeightFunction } from "../interfaces/IFacetBase.sol";

/**
 * @title PolicyFacet
 * @author Venus
 * @dev This facet contains all the hooks used while transferring the assets
 * @notice This facet contract contains all the external pre-hook functions related to vToken
 */
contract PolicyFacet is IPolicyFacet, XVSRewardsHelper {
    /// @notice Emitted when a new borrow-side XVS speed is calculated for a market
    event VenusBorrowSpeedUpdated(VToken indexed vToken, uint256 newSpeed);

    /// @notice Emitted when a new supply-side XVS speed is calculated for a market
    event VenusSupplySpeedUpdated(VToken indexed vToken, uint256 newSpeed);

    /**
     * @notice Checks if the account should be allowed to mint tokens in the given market
     * @param vToken The market to verify the mint against
     * @param minter The account which would get the minted tokens
     * @param mintAmount The amount of underlying being supplied to the market in exchange for tokens
     * @return 0 if the mint is allowed, otherwise a semi-opaque error code (See ErrorReporter.sol)
     */
    function mintAllowed(address vToken, address minter, uint256 mintAmount) external returns (uint256) {
        // Pausing is a very serious situation - we revert to sound the alarms
        checkProtocolPauseState();
        checkActionPauseState(vToken, Action.MINT);
        ensureListed(getCorePoolMarket(vToken));

        uint256 supplyCap = supplyCaps[vToken];
        require(supplyCap != 0, "market supply cap is 0");

        uint256 vTokenSupply = VToken(vToken).totalSupply();
        Exp memory exchangeRate = Exp({ mantissa: VToken(vToken).exchangeRateStored() });
        uint256 nextTotalSupply = mul_ScalarTruncateAddUInt(exchangeRate, vTokenSupply, mintAmount);

        require(nextTotalSupply <= supplyCap, "market supply cap reached");

        // Keep the flywheel moving
        updateVenusSupplyIndex(vToken);
        distributeSupplierVenus(vToken, minter);

        return uint256(Error.NO_ERROR);
    }

    /**
     * @notice Validates mint, accrues interest and updates score in prime. Reverts on rejection. May emit logs.
     * @param vToken Asset being minted
     * @param minter The address minting the tokens
     * @param actualMintAmount The amount of the underlying asset being minted
     * @param mintTokens The number of tokens being minted
     */
    // solhint-disable-next-line no-unused-vars
    function mintVerify(address vToken, address minter, uint256 actualMintAmount, uint256 mintTokens) external {
        if (address(prime) != address(0)) {
            prime.accrueInterestAndUpdateScore(minter, vToken);
        }
    }

    /**
     * @notice Checks if the account should be allowed to redeem tokens in the given market
     * @param vToken The market to verify the redeem against
     * @param redeemer The account which would redeem the tokens
     * @param redeemTokens The number of vTokens to exchange for the underlying asset in the market
     * @return 0 if the redeem is allowed, otherwise a semi-opaque error code (See ErrorReporter.sol)
     */
    function redeemAllowed(address vToken, address redeemer, uint256 redeemTokens) external returns (uint256) {
        checkProtocolPauseState();
        checkActionPauseState(vToken, Action.REDEEM);

        uint256 allowed = redeemAllowedInternal(vToken, redeemer, redeemTokens);
        if (allowed != uint256(Error.NO_ERROR)) {
            return allowed;
        }

        // Keep the flywheel moving
        updateVenusSupplyIndex(vToken);
        distributeSupplierVenus(vToken, redeemer);

        return uint256(Error.NO_ERROR);
    }

    /**
     * @notice Validates redeem, accrues interest and updates score in prime. Reverts on rejection. May emit logs.
     * @param vToken Asset being redeemed
     * @param redeemer The address redeeming the tokens
     * @param redeemAmount The amount of the underlying asset being redeemed
     * @param redeemTokens The number of tokens being redeemed
     */
    function redeemVerify(address vToken, address redeemer, uint256 redeemAmount, uint256 redeemTokens) external {
        require(redeemTokens != 0 || redeemAmount == 0, "redeemTokens zero");
        if (address(prime) != address(0)) {
            prime.accrueInterestAndUpdateScore(redeemer, vToken);
        }
    }

    /**
     * @notice Checks if the account should be allowed to borrow the underlying asset of the given market
     * @param vToken The market to verify the borrow against
     * @param borrower The account which would borrow the asset
     * @param borrowAmount The amount of underlying the account would borrow
     * @return 0 if the borrow is allowed, otherwise a semi-opaque error code (See ErrorReporter.sol)
     */
    function borrowAllowed(address vToken, address borrower, uint256 borrowAmount) external returns (uint256) {
        // Pausing is a very serious situation - we revert to sound the alarms
        checkProtocolPauseState();
        checkActionPauseState(vToken, Action.BORROW);
        ensureListed(getCorePoolMarket(vToken));
        poolBorrowAllowed(borrower, vToken);

        uint256 borrowCap = borrowCaps[vToken];
        require(borrowCap != 0, "market borrow cap is 0");

        if (!getCorePoolMarket(vToken).accountMembership[borrower]) {
            // only vTokens may call borrowAllowed if borrower not in market
            require(msg.sender == vToken, "sender must be vToken");

            // attempt to add borrower to the market
            Error err = addToMarketInternal(VToken(vToken), borrower);
            if (err != Error.NO_ERROR) {
                return uint256(err);
            }
        }

        if (oracle.getUnderlyingPrice(vToken) == 0) {
            return uint256(Error.PRICE_ERROR);
        }

        uint256 nextTotalBorrows = add_(VToken(vToken).totalBorrows(), borrowAmount);
        require(nextTotalBorrows <= borrowCap, "market borrow cap reached");

        (Error err, , uint256 shortfall) = getHypotheticalAccountLiquidityInternal(
            borrower,
            VToken(vToken),
            0,
            borrowAmount,
            WeightFunction.USE_COLLATERAL_FACTOR
        );
        if (err != Error.NO_ERROR) {
            return uint256(err);
        }

        if (shortfall != 0) {
            return uint256(Error.INSUFFICIENT_LIQUIDITY);
        }

        // Keep the flywheel moving
        Exp memory borrowIndex = Exp({ mantissa: VToken(vToken).borrowIndex() });
        updateVenusBorrowIndex(vToken, borrowIndex);
        distributeBorrowerVenus(vToken, borrower, borrowIndex);

        return uint256(Error.NO_ERROR);
    }

    /**
     * @notice Validates borrow, accrues interest and updates score in prime. Reverts on rejection. May emit logs.
     * @param vToken Asset whose underlying is being borrowed
     * @param borrower The address borrowing the underlying
     * @param borrowAmount The amount of the underlying asset requested to borrow
     */
    // solhint-disable-next-line no-unused-vars
    function borrowVerify(address vToken, address borrower, uint256 borrowAmount) external {
        if (address(prime) != address(0)) {
            prime.accrueInterestAndUpdateScore(borrower, vToken);
        }
    }

    /**
     * @notice Checks if the account should be allowed to repay a borrow in the given market
     * @param vToken The market to verify the repay against
     * @param payer The account which would repay the asset
     * @param borrower The account which borrowed the asset
     * @param repayAmount The amount of the underlying asset the account would repay
     * @return 0 if the repay is allowed, otherwise a semi-opaque error code (See ErrorReporter.sol)
     */
    function repayBorrowAllowed(
        address vToken,
        address payer, // solhint-disable-line no-unused-vars
        address borrower,
        uint256 repayAmount // solhint-disable-line no-unused-vars
    ) external returns (uint256) {
        checkProtocolPauseState();
        checkActionPauseState(vToken, Action.REPAY);
        ensureListed(getCorePoolMarket(vToken));

        // Keep the flywheel moving
        Exp memory borrowIndex = Exp({ mantissa: VToken(vToken).borrowIndex() });
        updateVenusBorrowIndex(vToken, borrowIndex);
        distributeBorrowerVenus(vToken, borrower, borrowIndex);

        return uint256(Error.NO_ERROR);
    }

    /**
     * @notice Validates repayBorrow, accrues interest and updates score in prime. Reverts on rejection. May emit logs.
     * @param vToken Asset being repaid
     * @param payer The address repaying the borrow
     * @param borrower The address of the borrower
     * @param actualRepayAmount The amount of underlying being repaid
     */
    function repayBorrowVerify(
        address vToken,
        address payer, // solhint-disable-line no-unused-vars
        address borrower,
        uint256 actualRepayAmount, // solhint-disable-line no-unused-vars
        uint256 borrowerIndex // solhint-disable-line no-unused-vars
    ) external {
        if (address(prime) != address(0)) {
            prime.accrueInterestAndUpdateScore(borrower, vToken);
        }
    }

    /**
     * @notice Checks if the liquidation should be allowed to occur
     * @param vTokenBorrowed Asset which was borrowed by the borrower
     * @param vTokenCollateral Asset which was used as collateral and will be seized
     * @param liquidator The address repaying the borrow and seizing the collateral
     * @param borrower The address of the borrower
     * @param repayAmount The amount of underlying being repaid
     */
    function liquidateBorrowAllowed(
        address vTokenBorrowed,
        address vTokenCollateral,
        address liquidator,
        address borrower,
        uint256 repayAmount
    ) external view returns (uint256) {
        checkProtocolPauseState();

        // if we want to pause liquidating to vTokenCollateral, we should pause seizing
        checkActionPauseState(vTokenBorrowed, Action.LIQUIDATE);

        if (liquidatorContract != address(0) && liquidator != liquidatorContract) {
            return uint256(Error.UNAUTHORIZED);
        }

        ensureListed(getCorePoolMarket(vTokenCollateral));
        uint256 borrowBalance;
        if (address(vTokenBorrowed) != address(vaiController)) {
            ensureListed(getCorePoolMarket(vTokenBorrowed));
            borrowBalance = VToken(vTokenBorrowed).borrowBalanceStored(borrower);
        } else {
            borrowBalance = vaiController.getVAIRepayAmount(borrower);
        }

        if (isForcedLiquidationEnabled[vTokenBorrowed] || isForcedLiquidationEnabledForUser[borrower][vTokenBorrowed]) {
            if (repayAmount > borrowBalance) {
                return uint(Error.TOO_MUCH_REPAY);
            }
            return uint(Error.NO_ERROR);
        }

        /* The borrower must have shortfall in order to be liquidatable */
        (Error err, , uint256 shortfall) = getHypotheticalAccountLiquidityInternal(
            borrower,
            VToken(address(0)),
            0,
            0,
            WeightFunction.USE_LIQUIDATION_THRESHOLD
        );

        if (err != Error.NO_ERROR) {
            return uint256(err);
        }
        if (shortfall == 0) {
            return uint256(Error.INSUFFICIENT_SHORTFALL);
        }

        // The liquidator may not repay more than what is allowed by the closeFactor
        //-- maxClose = multipy of closeFactorMantissa and borrowBalance
        if (repayAmount > mul_ScalarTruncate(Exp({ mantissa: closeFactorMantissa }), borrowBalance)) {
            return uint256(Error.TOO_MUCH_REPAY);
        }

        return uint256(Error.NO_ERROR);
    }

    /**
     * @notice Validates liquidateBorrow, accrues interest and updates score in prime. Reverts on rejection. May emit logs.
     * @param vTokenBorrowed Asset which was borrowed by the borrower
     * @param vTokenCollateral Asset which was used as collateral and will be seized
     * @param liquidator The address repaying the borrow and seizing the collateral
     * @param borrower The address of the borrower
     * @param actualRepayAmount The amount of underlying being repaid
     * @param seizeTokens The amount of collateral token that will be seized
     */
    function liquidateBorrowVerify(
        address vTokenBorrowed,
        address vTokenCollateral, // solhint-disable-line no-unused-vars
        address liquidator,
        address borrower,
        uint256 actualRepayAmount, // solhint-disable-line no-unused-vars
        uint256 seizeTokens // solhint-disable-line no-unused-vars
    ) external {
        if (address(prime) != address(0)) {
            prime.accrueInterestAndUpdateScore(borrower, vTokenBorrowed);
            prime.accrueInterestAndUpdateScore(liquidator, vTokenBorrowed);
        }
    }

    /**
     * @notice Checks if the seizing of assets should be allowed to occur
     * @param vTokenCollateral Asset which was used as collateral and will be seized
     * @param vTokenBorrowed Asset which was borrowed by the borrower
     * @param liquidator The address repaying the borrow and seizing the collateral
     * @param borrower The address of the borrower
     * @param seizeTokens The number of collateral tokens to seize
     */
    function seizeAllowed(
        address vTokenCollateral,
        address vTokenBorrowed,
        address liquidator,
        address borrower,
        uint256 seizeTokens // solhint-disable-line no-unused-vars
    ) external returns (uint256) {
        // Pausing is a very serious situation - we revert to sound the alarms
        checkProtocolPauseState();
        checkActionPauseState(vTokenCollateral, Action.SEIZE);

        Market storage market = getCorePoolMarket(vTokenCollateral);

        // We've added VAIController as a borrowed token list check for seize
        ensureListed(market);

        if (!market.accountMembership[borrower]) {
            return uint256(Error.MARKET_NOT_COLLATERAL);
        }

        if (address(vTokenBorrowed) != address(vaiController)) {
            ensureListed(getCorePoolMarket(vTokenBorrowed));
        }

        if (VToken(vTokenCollateral).comptroller() != VToken(vTokenBorrowed).comptroller()) {
            return uint256(Error.COMPTROLLER_MISMATCH);
        }

        // Keep the flywheel moving
        updateVenusSupplyIndex(vTokenCollateral);
        distributeSupplierVenus(vTokenCollateral, borrower);
        distributeSupplierVenus(vTokenCollateral, liquidator);

        return uint256(Error.NO_ERROR);
    }

    /**
     * @notice Validates seize, accrues interest and updates score in prime. Reverts on rejection. May emit logs.
     * @param vTokenCollateral Asset which was used as collateral and will be seized
     * @param vTokenBorrowed Asset which was borrowed by the borrower
     * @param liquidator The address repaying the borrow and seizing the collateral
     * @param borrower The address of the borrower
     * @param seizeTokens The number of collateral tokens to seize
     */
    function seizeVerify(
        address vTokenCollateral,
        address vTokenBorrowed, // solhint-disable-line no-unused-vars
        address liquidator,
        address borrower,
        uint256 seizeTokens // solhint-disable-line no-unused-vars
    ) external {
        if (address(prime) != address(0)) {
            prime.accrueInterestAndUpdateScore(borrower, vTokenCollateral);
            prime.accrueInterestAndUpdateScore(liquidator, vTokenCollateral);
        }
    }

    /**
     * @notice Checks if the account should be allowed to transfer tokens in the given market
     * @param vToken The market to verify the transfer against
     * @param src The account which sources the tokens
     * @param dst The account which receives the tokens
     * @param transferTokens The number of vTokens to transfer
     * @return 0 if the transfer is allowed, otherwise a semi-opaque error code (See ErrorReporter.sol)
     */
    function transferAllowed(
        address vToken,
        address src,
        address dst,
        uint256 transferTokens
    ) external returns (uint256) {
        // Pausing is a very serious situation - we revert to sound the alarms
        checkProtocolPauseState();
        checkActionPauseState(vToken, Action.TRANSFER);

        // Currently the only consideration is whether or not
        //  the src is allowed to redeem this many tokens
        uint256 allowed = redeemAllowedInternal(vToken, src, transferTokens);
        if (allowed != uint256(Error.NO_ERROR)) {
            return allowed;
        }

        // Keep the flywheel moving
        updateVenusSupplyIndex(vToken);
        distributeSupplierVenus(vToken, src);
        distributeSupplierVenus(vToken, dst);

        return uint256(Error.NO_ERROR);
    }

    /**
     * @notice Validates transfer, accrues interest and updates score in prime. Reverts on rejection. May emit logs.
     * @param vToken Asset being transferred
     * @param src The account which sources the tokens
     * @param dst The account which receives the tokens
     * @param transferTokens The number of vTokens to transfer
     */
    // solhint-disable-next-line no-unused-vars
    function transferVerify(address vToken, address src, address dst, uint256 transferTokens) external {
        if (address(prime) != address(0)) {
            prime.accrueInterestAndUpdateScore(src, vToken);
            prime.accrueInterestAndUpdateScore(dst, vToken);
        }
    }

    /**
     * @notice Alias to getAccountLiquidity to support the Isolated Lending Comptroller Interface
     * @param account The account get liquidity for
     * @return (possible error code (semi-opaque),
                account liquidity in excess of collateral requirements,
     *          account shortfall below collateral requirements)
     */
    function getBorrowingPower(address account) external view returns (uint256, uint256, uint256) {
        return _getAccountLiquidity(account, WeightFunction.USE_COLLATERAL_FACTOR);
    }

    /**
     * @notice Determine the current account liquidity wrt liquidation threshold requirements
     * @param account The account get liquidity for
     * @return (possible error code (semi-opaque),
                account liquidity in excess of liquidation threshold requirements,
     *          account shortfall below liquidation threshold requirements)
     */
    function getAccountLiquidity(address account) external view returns (uint256, uint256, uint256) {
        return _getAccountLiquidity(account, WeightFunction.USE_LIQUIDATION_THRESHOLD);
    }

    /**
     * @notice Determine what the account liquidity would be if the given amounts were redeemed/borrowed
     * @param vTokenModify The market to hypothetically redeem/borrow in
     * @param account The account to determine liquidity for
     * @param redeemTokens The number of tokens to hypothetically redeem
     * @param borrowAmount The amount of underlying to hypothetically borrow
     * @return (possible error code (semi-opaque),
                hypothetical account liquidity in excess of collateral requirements,
     *          hypothetical account shortfall below collateral requirements)
     */
    function getHypotheticalAccountLiquidity(
        address account,
        address vTokenModify,
        uint256 redeemTokens,
        uint256 borrowAmount
    ) external view returns (uint256, uint256, uint256) {
        (Error err, uint256 liquidity, uint256 shortfall) = getHypotheticalAccountLiquidityInternal(
            account,
            VToken(vTokenModify),
            redeemTokens,
            borrowAmount,
            WeightFunction.USE_COLLATERAL_FACTOR
        );
        return (uint256(err), liquidity, shortfall);
    }

    // setter functionality
    /**
     * @notice Set XVS speed for a single market
     * @dev Allows the contract admin to set XVS speed for a market
     * @param vTokens The market whose XVS speed to update
     * @param supplySpeeds New XVS speed for supply
     * @param borrowSpeeds New XVS speed for borrow
     */
    function _setVenusSpeeds(
        VToken[] calldata vTokens,
        uint256[] calldata supplySpeeds,
        uint256[] calldata borrowSpeeds
    ) external {
        ensureAdmin();

        uint256 numTokens = vTokens.length;
        require(numTokens == supplySpeeds.length && numTokens == borrowSpeeds.length, "invalid input");

        for (uint256 i; i < numTokens; ++i) {
            ensureNonzeroAddress(address(vTokens[i]));
            setVenusSpeedInternal(vTokens[i], supplySpeeds[i], borrowSpeeds[i]);
        }
    }

    /**
     * @dev Internal function to set XVS speed for a single market
     * @param vToken The market whose XVS speed to update
     * @param supplySpeed New XVS speed for supply
     * @param borrowSpeed New XVS speed for borrow
     * @custom:event VenusSupplySpeedUpdated Emitted after the venus supply speed for a market is updated
     * @custom:event VenusBorrowSpeedUpdated Emitted after the venus borrow speed for a market is updated
     */
    function setVenusSpeedInternal(VToken vToken, uint256 supplySpeed, uint256 borrowSpeed) internal {
        ensureListed(getCorePoolMarket(address(vToken)));

        if (venusSupplySpeeds[address(vToken)] != supplySpeed) {
            // Supply speed updated so let's update supply state to ensure that
            //  1. XVS accrued properly for the old speed, and
            //  2. XVS accrued at the new speed starts after this block.

            updateVenusSupplyIndex(address(vToken));
            // Update speed and emit event
            venusSupplySpeeds[address(vToken)] = supplySpeed;
            emit VenusSupplySpeedUpdated(vToken, supplySpeed);
        }

        if (venusBorrowSpeeds[address(vToken)] != borrowSpeed) {
            // Borrow speed updated so let's update borrow state to ensure that
            //  1. XVS accrued properly for the old speed, and
            //  2. XVS accrued at the new speed starts after this block.
            Exp memory borrowIndex = Exp({ mantissa: vToken.borrowIndex() });
            updateVenusBorrowIndex(address(vToken), borrowIndex);

            // Update speed and emit event
            venusBorrowSpeeds[address(vToken)] = borrowSpeed;
            emit VenusBorrowSpeedUpdated(vToken, borrowSpeed);
        }
    }

    /**
     * @dev Checks if vToken borrowing is allowed in the account's entered pool
     *      Reverts if borrowing is not permitted
     * @param account The address of the account whose borrow permission is being checked
     * @param vToken The vToken market to check borrowing status for
     * @custom:error BorrowNotAllowedInPool Reverts if borrowing is not allowed in the account's entered pool
     * @custom:error InactivePool Reverts if borrowing in an inactive pool.
     */
    function poolBorrowAllowed(address account, address vToken) internal view {
        uint96 userPool = userPoolId[account];
        PoolMarketId index = getPoolMarketIndex(userPool, vToken);
        if (!_poolMarkets[index].isBorrowAllowed) {
            revert BorrowNotAllowedInPool();
        }
        if (userPool != corePoolId && !pools[userPool].isActive) {
            revert InactivePool(userPool);
        }
    }
}

contracts/Comptroller/Diamond/facets/Rew.content:
// SPDX-License-Identifier: BSD-3-Clause

pragma solidity 0.8.25;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import { VToken } from "../../../Tokens/VTokens/VToken.sol";
import { IRewardFacet } from "../interfaces/IRewardFacet.sol";
import { XVSRewardsHelper } from "./XVSRewardsHelper.sol";
import { VBep20Interface } from "../../../Tokens/VTokens/VTokenInterfaces.sol";
import { WeightFunction } from "../interfaces/IFacetBase.sol";

/**
 * @title RewardFacet
 * @author Venus
 * @dev This facet contains all the methods related to the reward functionality
 * @notice This facet contract provides the external functions related to all claims and rewards of the protocol
 */
contract RewardFacet is IRewardFacet, XVSRewardsHelper {
    /// @notice Emitted when Venus is granted by admin
    event VenusGranted(address indexed recipient, uint256 amount);

    /// @notice Emitted when XVS are seized for the holder
    event VenusSeized(address indexed holder, uint256 amount);

    using SafeERC20 for IERC20;

    /**
     * @notice Claim all the xvs accrued by holder in all markets and VAI
     * @param holder The address to claim XVS for
     */
    function claimVenus(address holder) public {
        return claimVenus(holder, allMarkets);
    }

    /**
     * @notice Claim all the xvs accrued by holder in the specified markets
     * @param holder The address to claim XVS for
     * @param vTokens The list of markets to claim XVS in
     */
    function claimVenus(address holder, VToken[] memory vTokens) public {
        address[] memory holders = new address[](1);
        holders[0] = holder;
        claimVenus(holders, vTokens, true, true);
    }

    /**
     * @notice Claim all xvs accrued by the holders
     * @param holders The addresses to claim XVS for
     * @param vTokens The list of markets to claim XVS in
     * @param borrowers Whether or not to claim XVS earned by borrowing
     * @param suppliers Whether or not to claim XVS earned by supplying
     */
    function claimVenus(address[] memory holders, VToken[] memory vTokens, bool borrowers, bool suppliers) public {
        claimVenus(holders, vTokens, borrowers, suppliers, false);
    }

    /**
     * @notice Claim all the xvs accrued by holder in all markets, a shorthand for `claimVenus` with collateral set to `true`
     * @param holder The address to claim XVS for
     */
    function claimVenusAsCollateral(address holder) external {
        address[] memory holders = new address[](1);
        holders[0] = holder;
        claimVenus(holders, allMarkets, true, true, true);
    }

    /**
     * @notice Transfer XVS to the user with user's shortfall considered
     * @dev Note: If there is not enough XVS, we do not perform the transfer all
     * @param user The address of the user to transfer XVS to
     * @param amount The amount of XVS to (possibly) transfer
     * @param shortfall The shortfall of the user
     * @param collateral Whether or not we will use user's venus reward as collateral to pay off the debt
     * @return The amount of XVS which was NOT transferred to the user
     */
    function grantXVSInternal(
        address user,
        uint256 amount,
        uint256 shortfall,
        bool collateral
    ) internal returns (uint256) {
        // If the user is blacklisted, they can't get XVS rewards
        require(
            user != 0xEF044206Db68E40520BfA82D45419d498b4bc7Bf &&
                user != 0x7589dD3355DAE848FDbF75044A3495351655cB1A &&
                user != 0x33df7a7F6D44307E1e5F3B15975b47515e5524c0 &&
                user != 0x24e77E5b74B30b026E9996e4bc3329c881e24968,
            "Blacklisted"
        );

        IERC20 xvs_ = IERC20(xvs);

        if (amount == 0 || amount > xvs_.balanceOf(address(this))) {
            return amount;
        }

        if (shortfall == 0) {
            xvs_.safeTransfer(user, amount);
            return 0;
        }
        // If user's bankrupt and doesn't use pending xvs as collateral, don't grant
        // anything, otherwise, we will transfer the pending xvs as collateral to
        // vXVS token and mint vXVS for the user
        //
        // If mintBehalf failed, don't grant any xvs
        require(collateral, "bankrupt");

        address xvsVToken_ = xvsVToken;

        xvs_.safeApprove(xvsVToken_, 0);
        xvs_.safeApprove(xvsVToken_, amount);
        require(VBep20Interface(xvsVToken_).mintBehalf(user, amount) == uint256(Error.NO_ERROR), "mint behalf error");

        // set venusAccrued[user] to 0
        return 0;
    }

    /*** Venus Distribution Admin ***/

    /**
     * @notice Transfer XVS to the recipient
     * @dev Allows the contract admin to transfer XVS to any recipient based on the recipient's shortfall
     *      Note: If there is not enough XVS, we do not perform the transfer all
     * @param recipient The address of the recipient to transfer XVS to
     * @param amount The amount of XVS to (possibly) transfer
     */
    function _grantXVS(address recipient, uint256 amount) external {
        ensureAdmin();
        uint256 amountLeft = grantXVSInternal(recipient, amount, 0, false);
        require(amountLeft == 0, "no xvs");
        emit VenusGranted(recipient, amount);
    }

    /**
     * @dev Seize XVS tokens from the specified holders and transfer to recipient
     * @notice Seize XVS rewards allocated to holders
     * @param holders Addresses of the XVS holders
     * @param recipient Address of the XVS token recipient
     * @return The total amount of XVS tokens seized and transferred to recipient
     */
    function seizeVenus(address[] calldata holders, address recipient) external returns (uint256) {
        ensureAllowed("seizeVenus(address[],address)");

        uint256 holdersLength = holders.length;
        uint256 totalHoldings;

        updateAndDistributeRewardsInternal(holders, allMarkets, true, true);
        for (uint256 i; i < holdersLength; ++i) {
            address holder = holders[i];
            uint256 userHolding = venusAccrued[holder];

            if (userHolding != 0) {
                totalHoldings += userHolding;
                delete venusAccrued[holder];
            }

            emit VenusSeized(holder, userHolding);
        }

        if (totalHoldings != 0) {
            IERC20(xvs).safeTransfer(recipient, totalHoldings);
            emit VenusGranted(recipient, totalHoldings);
        }

        return totalHoldings;
    }

    /**
     * @notice Claim all xvs accrued by the holders
     * @param holders The addresses to claim XVS for
     * @param vTokens The list of markets to claim XVS in
     * @param borrowers Whether or not to claim XVS earned by borrowing
     * @param suppliers Whether or not to claim XVS earned by supplying
     * @param collateral Whether or not to use XVS earned as collateral, only takes effect when the holder has a shortfall
     */
    function claimVenus(
        address[] memory holders,
        VToken[] memory vTokens,
        bool borrowers,
        bool suppliers,
        bool collateral
    ) public {
        uint256 holdersLength = holders.length;

        updateAndDistributeRewardsInternal(holders, vTokens, borrowers, suppliers);
        for (uint256 j; j < holdersLength; ++j) {
            address holder = holders[j];

            // If there is a positive shortfall, the XVS reward is accrued,
            // but won't be granted to this holder
            (, , uint256 shortfall) = getHypotheticalAccountLiquidityInternal(
                holder,
                VToken(address(0)),
                0,
                0,
                WeightFunction.USE_COLLATERAL_FACTOR
            );

            uint256 value = venusAccrued[holder];
            delete venusAccrued[holder];

            uint256 returnAmount = grantXVSInternal(holder, value, shortfall, collateral);

            // returnAmount can only be positive if balance of xvsAddress is less than grant amount(venusAccrued[holder])
            if (returnAmount != 0) {
                venusAccrued[holder] = returnAmount;
            }
        }
    }

    /**
     * @notice Update and distribute tokens
     * @param holders The addresses to claim XVS for
     * @param vTokens The list of markets to claim XVS in
     * @param borrowers Whether or not to claim XVS earned by borrowing
     * @param suppliers Whether or not to claim XVS earned by supplying
     */
    function updateAndDistributeRewardsInternal(
        address[] memory holders,
        VToken[] memory vTokens,
        bool borrowers,
        bool suppliers
    ) internal {
        uint256 j;
        uint256 holdersLength = holders.length;
        uint256 vTokensLength = vTokens.length;

        for (uint256 i; i < vTokensLength; ++i) {
            VToken vToken = vTokens[i];
            ensureListed(getCorePoolMarket(address(vToken)));
            if (borrowers) {
                Exp memory borrowIndex = Exp({ mantissa: vToken.borrowIndex() });
                updateVenusBorrowIndex(address(vToken), borrowIndex);
                for (j = 0; j < holdersLength; ++j) {
                    distributeBorrowerVenus(address(vToken), holders[j], borrowIndex);
                }
            }

            if (suppliers) {
                updateVenusSupplyIndex(address(vToken));
                for (j = 0; j < holdersLength; ++j) {
                    distributeSupplierVenus(address(vToken), holders[j]);
                }
            }
        }
    }

    /**
     * @notice Returns the XVS vToken address
     * @return The address of XVS vToken
     */
    function getXVSVTokenAddress() external view returns (address) {
        return xvsVToken;
    }
}

contracts/Comptroller/Diamond/facets/Set.content:
// SPDX-License-Identifier: BSD-3-Clause

pragma solidity 0.8.25;

import { ResilientOracleInterface } from "@venusprotocol/oracle/contracts/interfaces/OracleInterface.sol";

import { VToken } from "../../../Tokens/VTokens/VToken.sol";
import { Action } from "../../ComptrollerInterface.sol";
import { ComptrollerLensInterface } from "../../ComptrollerLensInterface.sol";
import { VAIControllerInterface } from "../../../Tokens/VAI/VAIControllerInterface.sol";
import { IPrime } from "../../../Tokens/Prime/IPrime.sol";
import { ISetterFacet } from "../interfaces/ISetterFacet.sol";
import { FacetBase } from "./FacetBase.sol";
import { PoolMarketId } from "../../../Comptroller/Types/PoolMarketId.sol";

/**
 * @title SetterFacet
 * @author Venus
 * @dev This facet contains all the setters for the states
 * @notice This facet contract contains all the configurational setter functions
 */
contract SetterFacet is ISetterFacet, FacetBase {
    /// @notice Emitted when close factor is changed by admin
    event NewCloseFactor(uint256 oldCloseFactorMantissa, uint256 newCloseFactorMantissa);

    /// @notice Emitted when a collateral factor for a market in a pool is changed by admin
    event NewCollateralFactor(
        uint96 indexed poolId,
        VToken indexed vToken,
        uint256 oldCollateralFactorMantissa,
        uint256 newCollateralFactorMantissa
    );

    /// @notice Emitted when liquidation incentive for a market in a pool is changed by admin
    event NewLiquidationIncentive(
        uint96 indexed poolId,
        address indexed vToken,
        uint256 oldLiquidationIncentiveMantissa,
        uint256 newLiquidationIncentiveMantissa
    );

    /// @notice Emitted when price oracle is changed
    event NewPriceOracle(ResilientOracleInterface oldPriceOracle, ResilientOracleInterface newPriceOracle);

    /// @notice Emitted when borrow cap for a vToken is changed
    event NewBorrowCap(VToken indexed vToken, uint256 newBorrowCap);

    /// @notice Emitted when VAIController is changed
    event NewVAIController(VAIControllerInterface oldVAIController, VAIControllerInterface newVAIController);

    /// @notice Emitted when VAI mint rate is changed by admin
    event NewVAIMintRate(uint256 oldVAIMintRate, uint256 newVAIMintRate);

    /// @notice Emitted when protocol state is changed by admin
    event ActionProtocolPaused(bool state);

    /// @notice Emitted when treasury guardian is changed
    event NewTreasuryGuardian(address oldTreasuryGuardian, address newTreasuryGuardian);

    /// @notice Emitted when treasury address is changed
    event NewTreasuryAddress(address oldTreasuryAddress, address newTreasuryAddress);

    /// @notice Emitted when treasury percent is changed
    event NewTreasuryPercent(uint256 oldTreasuryPercent, uint256 newTreasuryPercent);

    /// @notice Emitted when liquidator adress is changed
    event NewLiquidatorContract(address oldLiquidatorContract, address newLiquidatorContract);

    /// @notice Emitted when ComptrollerLens address is changed
    event NewComptrollerLens(address oldComptrollerLens, address newComptrollerLens);

    /// @notice Emitted when supply cap for a vToken is changed
    event NewSupplyCap(VToken indexed vToken, uint256 newSupplyCap);

    /// @notice Emitted when access control address is changed by admin
    event NewAccessControl(address oldAccessControlAddress, address newAccessControlAddress);

    /// @notice Emitted when pause guardian is changed
    event NewPauseGuardian(address oldPauseGuardian, address newPauseGuardian);

    /// @notice Emitted when an action is paused on a market
    event ActionPausedMarket(VToken indexed vToken, Action indexed action, bool pauseState);

    /// @notice Emitted when VAI Vault info is changed
    event NewVAIVaultInfo(address indexed vault_, uint256 releaseStartBlock_, uint256 releaseInterval_);

    /// @notice Emitted when Venus VAI Vault rate is changed
    event NewVenusVAIVaultRate(uint256 oldVenusVAIVaultRate, uint256 newVenusVAIVaultRate);

    /// @notice Emitted when prime token contract address is changed
    event NewPrimeToken(IPrime oldPrimeToken, IPrime newPrimeToken);

    /// @notice Emitted when forced liquidation is enabled or disabled for all users in a market
    event IsForcedLiquidationEnabledUpdated(address indexed vToken, bool enable);

    /// @notice Emitted when forced liquidation is enabled or disabled for a user borrowing in a market
    event IsForcedLiquidationEnabledForUserUpdated(address indexed borrower, address indexed vToken, bool enable);

    /// @notice Emitted when XVS token address is changed
    event NewXVSToken(address indexed oldXVS, address indexed newXVS);

    /// @notice Emitted when XVS vToken address is changed
    event NewXVSVToken(address indexed oldXVSVToken, address indexed newXVSVToken);

    /// @notice Emitted when an account's flash loan whitelist status is updated
    event IsAccountFlashLoanWhitelisted(address indexed account, bool indexed isWhitelisted);

    /// @notice Emitted when liquidation threshold for a market in a pool is changed by admin
    event NewLiquidationThreshold(
        uint96 indexed poolId,
        VToken indexed vToken,
        uint256 oldLiquidationThresholdMantissa,
        uint256 newLiquidationThresholdMantissa
    );

    /// @notice Emitted when the borrowAllowed flag is updated for a market
    event BorrowAllowedUpdated(uint96 indexed poolId, address indexed market, bool oldStatus, bool newStatus);

    /// @notice Emitted when pool active status updated
    event PoolActiveStatusUpdated(uint96 indexed poolId, bool oldStatus, bool newStatus);

    /// @notice Emitted when pool label is updated
    event PoolLabelUpdated(uint96 indexed poolId, string oldLabel, string newLabel);

    /// @notice Emitted when pool Fallback status is updated
    event PoolFallbackStatusUpdated(uint96 indexed poolId, bool oldStatus, bool newStatus);

    /**
     * @notice Compare two addresses to ensure they are different
     * @param oldAddress The original address to compare
     * @param newAddress The new address to compare
     */
    modifier compareAddress(address oldAddress, address newAddress) {
        require(oldAddress != newAddress, "old address is same as new address");
        _;
    }

    /**
     * @notice Compare two values to ensure they are different
     * @param oldValue The original value to compare
     * @param newValue The new value to compare
     */
    modifier compareValue(uint256 oldValue, uint256 newValue) {
        require(oldValue != newValue, "old value is same as new value");
        _;
    }

    /**
     * @notice Alias to _setPriceOracle to support the Isolated Lending Comptroller Interface
     * @param newOracle The new price oracle to set
     * @return uint256 0=success, otherwise a failure (see ErrorReporter.sol for details)
     */
    function setPriceOracle(ResilientOracleInterface newOracle) external returns (uint256) {
        return __setPriceOracle(newOracle);
    }

    /**
     * @notice Sets a new price oracle for the comptroller
     * @dev Allows the contract admin to set a new price oracle used by the Comptroller
     * @param newOracle The new price oracle to set
     * @return uint256 0=success, otherwise a failure (see ErrorReporter.sol for details)
     */
    function _setPriceOracle(ResilientOracleInterface newOracle) external returns (uint256) {
        return __setPriceOracle(newOracle);
    }

    /**
     * @notice Alias to _setCloseFactor to support the Isolated Lending Comptroller Interface
     * @param newCloseFactorMantissa New close factor, scaled by 1e18
     * @return uint256 0=success, otherwise will revert
     */
    function setCloseFactor(uint256 newCloseFactorMantissa) external returns (uint256) {
        return __setCloseFactor(newCloseFactorMantissa);
    }

    /**
     * @notice Sets the closeFactor used when liquidating borrows
     * @dev Allows the contract admin to set the closeFactor used to liquidate borrows
     * @param newCloseFactorMantissa New close factor, scaled by 1e18
     * @return uint256 0=success, otherwise will revert
     */
    function _setCloseFactor(uint256 newCloseFactorMantissa) external returns (uint256) {
        return __setCloseFactor(newCloseFactorMantissa);
    }

    /**
     * @notice Sets the address of the access control of this contract
     * @dev Allows the contract admin to set the address of access control of this contract
     * @param newAccessControlAddress New address for the access control
     * @return uint256 0=success, otherwise will revert
     */
    function _setAccessControl(
        address newAccessControlAddress
    ) external compareAddress(accessControl, newAccessControlAddress) returns (uint256) {
        // Check caller is admin
        ensureAdmin();
        ensureNonzeroAddress(newAccessControlAddress);

        address oldAccessControlAddress = accessControl;

        accessControl = newAccessControlAddress;
        emit NewAccessControl(oldAccessControlAddress, newAccessControlAddress);

        return uint256(Error.NO_ERROR);
    }

    /**
     * @notice Sets the collateral factor and liquidation threshold for a market in the Core Pool only.
     * @param vToken The market to set the factor on
     * @param newCollateralFactorMantissa The new collateral factor, scaled by 1e18
     * @param newLiquidationThresholdMantissa The new liquidation threshold, scaled by 1e18
     * @return uint256 0=success, otherwise a failure. (See ErrorReporter for details)
     */
    function setCollateralFactor(
        VToken vToken,
        uint256 newCollateralFactorMantissa,
        uint256 newLiquidationThresholdMantissa
    ) external returns (uint256) {
        ensureAllowed("setCollateralFactor(address,uint256,uint256)");
        return __setCollateralFactor(corePoolId, vToken, newCollateralFactorMantissa, newLiquidationThresholdMantissa);
    }

    /**
     * @notice Sets the liquidation incentive for a market in the Core Pool only.
     * @param vToken The market to set the liquidationIncentive for
     * @param newLiquidationIncentiveMantissa New liquidationIncentive scaled by 1e18
     * @return uint256 0=success, otherwise a failure. (See ErrorReporter for details)
     */
    function setLiquidationIncentive(
        address vToken,
        uint256 newLiquidationIncentiveMantissa
    ) external returns (uint256) {
        ensureAllowed("setLiquidationIncentive(address,uint256)");
        return __setLiquidationIncentive(corePoolId, vToken, newLiquidationIncentiveMantissa);
    }

    /**
     * @notice Sets the collateral factor and liquidation threshold for a market in the specified pool.
     * @param poolId The ID of the pool.
     * @param vToken The market to set the factor on
     * @param newCollateralFactorMantissa The new collateral factor, scaled by 1e18
     * @param newLiquidationThresholdMantissa The new liquidation threshold, scaled by 1e18
     * @return uint256 0=success, otherwise a failure. (See ErrorReporter for details)
     */
    function setCollateralFactor(
        uint96 poolId,
        VToken vToken,
        uint256 newCollateralFactorMantissa,
        uint256 newLiquidationThresholdMantissa
    ) external returns (uint256) {
        ensureAllowed("setCollateralFactor(uint96,address,uint256,uint256)");
        return __setCollateralFactor(poolId, vToken, newCollateralFactorMantissa, newLiquidationThresholdMantissa);
    }

    /**
     * @notice Sets the liquidation incentive for a market in the specified pool.
     * @param poolId The ID of the pool.
     * @param vToken The market to set the liquidationIncentive for
     * @param newLiquidationIncentiveMantissa New liquidationIncentive scaled by 1e18
     * @return uint256 0=success, otherwise a failure. (See ErrorReporter for details)
     */
    function setLiquidationIncentive(
        uint96 poolId,
        address vToken,
        uint256 newLiquidationIncentiveMantissa
    ) external returns (uint256) {
        ensureAllowed("setLiquidationIncentive(uint96,address,uint256)");
        return __setLiquidationIncentive(poolId, vToken, newLiquidationIncentiveMantissa);
    }

    /**
     * @notice Update the address of the liquidator contract
     * @dev Allows the contract admin to update the address of liquidator contract
     * @param newLiquidatorContract_ The new address of the liquidator contract
     */
    function _setLiquidatorContract(
        address newLiquidatorContract_
    ) external compareAddress(liquidatorContract, newLiquidatorContract_) {
        // Check caller is admin
        ensureAdmin();
        ensureNonzeroAddress(newLiquidatorContract_);
        address oldLiquidatorContract = liquidatorContract;
        liquidatorContract = newLiquidatorContract_;
        emit NewLiquidatorContract(oldLiquidatorContract, newLiquidatorContract_);
    }

    /**
     * @notice Admin function to change the Pause Guardian
     * @dev Allows the contract admin to change the Pause Guardian
     * @param newPauseGuardian The address of the new Pause Guardian
     * @return uint256 0=success, otherwise a failure. (See enum Error for details)
     */
    function _setPauseGuardian(
        address newPauseGuardian
    ) external compareAddress(pauseGuardian, newPauseGuardian) returns (uint256) {
        ensureAdmin();
        ensureNonzeroAddress(newPauseGuardian);

        // Save current value for inclusion in log
        address oldPauseGuardian = pauseGuardian;
        // Store pauseGuardian with value newPauseGuardian
        pauseGuardian = newPauseGuardian;

        // Emit NewPauseGuardian(OldPauseGuardian, NewPauseGuardian)
        emit NewPauseGuardian(oldPauseGuardian, newPauseGuardian);

        return uint256(Error.NO_ERROR);
    }

    /**
     * @notice Alias to _setMarketBorrowCaps to support the Isolated Lending Comptroller Interface
     * @param vTokens The addresses of the markets (tokens) to change the borrow caps for
     * @param newBorrowCaps The new borrow cap values in underlying to be set. A value of 0 corresponds to Borrow not allowed
     */
    function setMarketBorrowCaps(VToken[] calldata vTokens, uint256[] calldata newBorrowCaps) external {
        __setMarketBorrowCaps(vTokens, newBorrowCaps);
    }

    /**
     * @notice Set the given borrow caps for the given vToken market Borrowing that brings total borrows to or above borrow cap will revert
     * @dev Allows a privileged role to set the borrowing cap for a vToken market. A borrow cap of 0 corresponds to Borrow not allowed
     * @param vTokens The addresses of the markets (tokens) to change the borrow caps for
     * @param newBorrowCaps The new borrow cap values in underlying to be set. A value of 0 corresponds to Borrow not allowed
     */
    function _setMarketBorrowCaps(VToken[] calldata vTokens, uint256[] calldata newBorrowCaps) external {
        __setMarketBorrowCaps(vTokens, newBorrowCaps);
    }

    /**
     * @notice Alias to _setMarketSupplyCaps to support the Isolated Lending Comptroller Interface
     * @param vTokens The addresses of the markets (tokens) to change the supply caps for
     * @param newSupplyCaps The new supply cap values in underlying to be set. A value of 0 corresponds to Minting NotAllowed
     */
    function setMarketSupplyCaps(VToken[] calldata vTokens, uint256[] calldata newSupplyCaps) external {
        __setMarketSupplyCaps(vTokens, newSupplyCaps);
    }

    /**
     * @notice Set the given supply caps for the given vToken market Supply that brings total Supply to or above supply cap will revert
     * @dev Allows a privileged role to set the supply cap for a vToken. A supply cap of 0 corresponds to Minting NotAllowed
     * @param vTokens The addresses of the markets (tokens) to change the supply caps for
     * @param newSupplyCaps The new supply cap values in underlying to be set. A value of 0 corresponds to Minting NotAllowed
     */
    function _setMarketSupplyCaps(VToken[] calldata vTokens, uint256[] calldata newSupplyCaps) external {
        __setMarketSupplyCaps(vTokens, newSupplyCaps);
    }

    /**
     * @notice Set whole protocol pause/unpause state
     * @dev Allows a privileged role to pause/unpause protocol
     * @param state The new state (true=paused, false=unpaused)
     * @return bool The updated state of the protocol
     */
    function _setProtocolPaused(bool state) external returns (bool) {
        ensureAllowed("_setProtocolPaused(bool)");

        protocolPaused = state;
        emit ActionProtocolPaused(state);
        return state;
    }

    /**
     * @notice Alias to _setActionsPaused to support the Isolated Lending Comptroller Interface
     * @param markets_ Markets to pause/unpause the actions on
     * @param actions_ List of action ids to pause/unpause
     * @param paused_ The new paused state (true=paused, false=unpaused)
     */
    function setActionsPaused(address[] calldata markets_, Action[] calldata actions_, bool paused_) external {
        __setActionsPaused(markets_, actions_, paused_);
    }

    /**
     * @notice Pause/unpause certain actions
     * @dev Allows a privileged role to pause/unpause the protocol action state
     * @param markets_ Markets to pause/unpause the actions on
     * @param actions_ List of action ids to pause/unpause
     * @param paused_ The new paused state (true=paused, false=unpaused)
     */
    function _setActionsPaused(address[] calldata markets_, Action[] calldata actions_, bool paused_) external {
        __setActionsPaused(markets_, actions_, paused_);
    }

    /**
     * @dev Pause/unpause an action on a market
     * @param market Market to pause/unpause the action on
     * @param action Action id to pause/unpause
     * @param paused The new paused state (true=paused, false=unpaused)
     */
    function setActionPausedInternal(address market, Action action, bool paused) internal {
        ensureListed(getCorePoolMarket(market));
        _actionPaused[market][uint256(action)] = paused;
        emit ActionPausedMarket(VToken(market), action, paused);
    }

    /**
     * @notice Sets a new VAI controller
     * @dev Admin function to set a new VAI controller
     * @return uint256 0=success, otherwise a failure (see ErrorReporter.sol for details)
     */
    function _setVAIController(
        VAIControllerInterface vaiController_
    ) external compareAddress(address(vaiController), address(vaiController_)) returns (uint256) {
        // Check caller is admin
        ensureAdmin();
        ensureNonzeroAddress(address(vaiController_));

        VAIControllerInterface oldVaiController = vaiController;
        vaiController = vaiController_;
        emit NewVAIController(oldVaiController, vaiController_);

        return uint256(Error.NO_ERROR);
    }

    /**
     * @notice Set the VAI mint rate
     * @param newVAIMintRate The new VAI mint rate to be set
     * @return uint256 0=success, otherwise a failure (see ErrorReporter.sol for details)
     */
    function _setVAIMintRate(
        uint256 newVAIMintRate
    ) external compareValue(vaiMintRate, newVAIMintRate) returns (uint256) {
        // Check caller is admin
        ensureAdmin();
        uint256 oldVAIMintRate = vaiMintRate;
        vaiMintRate = newVAIMintRate;
        emit NewVAIMintRate(oldVAIMintRate, newVAIMintRate);

        return uint256(Error.NO_ERROR);
    }

    /**
     * @notice Set the minted VAI amount of the `owner`
     * @param owner The address of the account to set
     * @param amount The amount of VAI to set to the account
     * @return The number of minted VAI by `owner`
     */
    function setMintedVAIOf(address owner, uint256 amount) external returns (uint256) {
        checkProtocolPauseState();

        // Pausing is a very serious situation - we revert to sound the alarms
        require(!mintVAIGuardianPaused && !repayVAIGuardianPaused, "VAI is paused");
        // Check caller is vaiController
        if (msg.sender != address(vaiController)) {
            return fail(Error.REJECTION, FailureInfo.SET_MINTED_VAI_REJECTION);
        }
        mintedVAIs[owner] = amount;
        return uint256(Error.NO_ERROR);
    }

    /**
     * @notice Set the treasury data.
     * @param newTreasuryGuardian The new address of the treasury guardian to be set
     * @param newTreasuryAddress The new address of the treasury to be set
     * @param newTreasuryPercent The new treasury percent to be set
     * @return uint256 0=success, otherwise a failure (see ErrorReporter.sol for details)
     */
    function _setTreasuryData(
        address newTreasuryGuardian,
        address newTreasuryAddress,
        uint256 newTreasuryPercent
    ) external returns (uint256) {
        // Check caller is admin
        ensureAdminOr(treasuryGuardian);

        require(newTreasuryPercent < 1e18, "percent >= 100%");
        ensureNonzeroAddress(newTreasuryGuardian);
        ensureNonzeroAddress(newTreasuryAddress);

        address oldTreasuryGuardian = treasuryGuardian;
        address oldTreasuryAddress = treasuryAddress;
        uint256 oldTreasuryPercent = treasuryPercent;

        treasuryGuardian = newTreasuryGuardian;
        treasuryAddress = newTreasuryAddress;
        treasuryPercent = newTreasuryPercent;

        emit NewTreasuryGuardian(oldTreasuryGuardian, newTreasuryGuardian);
        emit NewTreasuryAddress(oldTreasuryAddress, newTreasuryAddress);
        emit NewTreasuryPercent(oldTreasuryPercent, newTreasuryPercent);

        return uint256(Error.NO_ERROR);
    }

    /*** Venus Distribution ***/

    /**
     * @dev Set ComptrollerLens contract address
     * @param comptrollerLens_ The new ComptrollerLens contract address to be set
     * @return uint256 0=success, otherwise a failure (see ErrorReporter.sol for details)
     */
    function _setComptrollerLens(
        ComptrollerLensInterface comptrollerLens_
    ) external virtual compareAddress(address(comptrollerLens), address(comptrollerLens_)) returns (uint256) {
        ensureAdmin();
        ensureNonzeroAddress(address(comptrollerLens_));
        address oldComptrollerLens = address(comptrollerLens);
        comptrollerLens = comptrollerLens_;
        emit NewComptrollerLens(oldComptrollerLens, address(comptrollerLens));

        return uint256(Error.NO_ERROR);
    }

    /**
     * @notice Set the amount of XVS distributed per block to VAI Vault
     * @param venusVAIVaultRate_ The amount of XVS wei per block to distribute to VAI Vault
     */
    function _setVenusVAIVaultRate(
        uint256 venusVAIVaultRate_
    ) external compareValue(venusVAIVaultRate, venusVAIVaultRate_) {
        ensureAdmin();
        if (vaiVaultAddress != address(0)) {
            releaseToVault();
        }
        uint256 oldVenusVAIVaultRate = venusVAIVaultRate;
        venusVAIVaultRate = venusVAIVaultRate_;
        emit NewVenusVAIVaultRate(oldVenusVAIVaultRate, venusVAIVaultRate_);
    }

    /**
     * @notice Set the VAI Vault infos
     * @param vault_ The address of the VAI Vault
     * @param releaseStartBlock_ The start block of release to VAI Vault
     * @param minReleaseAmount_ The minimum release amount to VAI Vault
     */
    function _setVAIVaultInfo(
        address vault_,
        uint256 releaseStartBlock_,
        uint256 minReleaseAmount_
    ) external compareAddress(vaiVaultAddress, vault_) {
        ensureAdmin();
        ensureNonzeroAddress(vault_);
        if (vaiVaultAddress != address(0)) {
            releaseToVault();
        }

        vaiVaultAddress = vault_;
        releaseStartBlock = releaseStartBlock_;
        minReleaseAmount = minReleaseAmount_;
        emit NewVAIVaultInfo(vault_, releaseStartBlock_, minReleaseAmount_);
    }

    /**
     * @notice Alias to _setPrimeToken to support the Isolated Lending Comptroller Interface
     * @param _prime The new prime token contract to be set
     * @return uint 0=success, otherwise a failure (see ErrorReporter.sol for details)
     */
    function setPrimeToken(IPrime _prime) external returns (uint256) {
        return __setPrimeToken(_prime);
    }

    /**
     * @notice Sets the prime token contract for the comptroller
     * @param _prime The new prime token contract to be set
     * @return uint 0=success, otherwise a failure (see ErrorReporter.sol for details)
     */
    function _setPrimeToken(IPrime _prime) external returns (uint256) {
        return __setPrimeToken(_prime);
    }

    /**
     * @notice Alias to _setForcedLiquidation to support the Isolated Lending Comptroller Interface
     * @param vTokenBorrowed Borrowed vToken
     * @param enable Whether to enable forced liquidations
     */
    function setForcedLiquidation(address vTokenBorrowed, bool enable) external {
        __setForcedLiquidation(vTokenBorrowed, enable);
    }

    /** @notice Enables forced liquidations for a market. If forced liquidation is enabled,
     * borrows in the market may be liquidated regardless of the account liquidity
     * @dev Allows a privileged role to set enable/disable forced liquidations
     * @param vTokenBorrowed Borrowed vToken
     * @param enable Whether to enable forced liquidations
     */
    function _setForcedLiquidation(address vTokenBorrowed, bool enable) external {
        __setForcedLiquidation(vTokenBorrowed, enable);
    }

    /**
     * @notice Enables forced liquidations for user's borrows in a certain market. If forced
     * liquidation is enabled, user's borrows in the market may be liquidated regardless of
     * the account liquidity. Forced liquidation may be enabled for a user even if it is not
     * enabled for the entire market.
     * @param borrower The address of the borrower
     * @param vTokenBorrowed Borrowed vToken
     * @param enable Whether to enable forced liquidations
     */
    function _setForcedLiquidationForUser(address borrower, address vTokenBorrowed, bool enable) external {
        ensureAllowed("_setForcedLiquidationForUser(address,address,bool)");
        if (vTokenBorrowed != address(vaiController)) {
            ensureListed(getCorePoolMarket(vTokenBorrowed));
        }
        isForcedLiquidationEnabledForUser[borrower][vTokenBorrowed] = enable;
        emit IsForcedLiquidationEnabledForUserUpdated(borrower, vTokenBorrowed, enable);
    }

    /**
     * @notice Set the address of the XVS token
     * @param xvs_ The address of the XVS token
     */
    function _setXVSToken(address xvs_) external {
        ensureAdmin();
        ensureNonzeroAddress(xvs_);

        emit NewXVSToken(xvs, xvs_);
        xvs = xvs_;
    }

    /**
     * @notice Set the address of the XVS vToken
     * @param xvsVToken_ The address of the XVS vToken
     */
    function _setXVSVToken(address xvsVToken_) external {
        ensureAdmin();
        ensureNonzeroAddress(xvsVToken_);

        address underlying = VToken(xvsVToken_).underlying();
        require(underlying == xvs, "invalid xvs vtoken address");

        emit NewXVSVToken(xvsVToken, xvsVToken_);
        xvsVToken = xvsVToken_;
    }

    /**
     * @notice Adds/Removes an account to the flash loan whitelist
     * @param account The account to authorize for flash loans
     * @param _isWhiteListed True to whitelist the account for flash loans, false to remove from whitelist
     */
    function setWhiteListFlashLoanAccount(address account, bool _isWhiteListed) external {
        ensureAllowed("setWhiteListFlashLoanAccount(address,bool)");
        ensureNonzeroAddress(account);

        authorizedFlashLoan[account] = _isWhiteListed;
        emit IsAccountFlashLoanWhitelisted(account, _isWhiteListed);
    }

    /**
     * @notice Updates the label for a specific pool (excluding the Core Pool)
     * @param poolId ID of the pool to update
     * @param newLabel The new label for the pool
     * @custom:error InvalidOperationForCorePool Reverts when attempting to call pool-specific methods on the Core Pool
     * @custom:error PoolDoesNotExist Reverts if the target pool ID does not exist
     * @custom:error EmptyPoolLabel Reverts if the provided label is an empty string
     * @custom:event PoolLabelUpdated Emitted after the pool label is updated
     */
    function setPoolLabel(uint96 poolId, string calldata newLabel) external {
        ensureAllowed("setPoolLabel(uint96,string)");

        if (poolId > lastPoolId) revert PoolDoesNotExist(poolId);
        if (poolId == corePoolId) revert InvalidOperationForCorePool();
        if (bytes(newLabel).length == 0) revert EmptyPoolLabel();

        PoolData storage pool = pools[poolId];

        if (keccak256(bytes(pool.label)) == keccak256(bytes(newLabel))) {
            return;
        }

        emit PoolLabelUpdated(poolId, pool.label, newLabel);
        pool.label = newLabel;
    }

    /**
     * @notice updates active status for a specific pool (excluding the Core Pool)
     * @param poolId id of the pool to update
     * @param active true to enable, false to disable
     * @custom:error InvalidOperationForCorePool Reverts when attempting to call pool-specific methods on the Core Pool.
     * @custom:error PoolDoesNotExist Reverts if the target pool ID does not exist.
     * @custom:event PoolActiveStatusUpdated Emitted after the pool active status is updated.
     */
    function setPoolActive(uint96 poolId, bool active) external {
        ensureAllowed("setPoolActive(uint96,bool)");

        if (poolId > lastPoolId) revert PoolDoesNotExist(poolId);
        if (poolId == corePoolId) revert InvalidOperationForCorePool();

        PoolData storage pool = pools[poolId];

        if (pool.isActive == active) {
            return;
        }

        emit PoolActiveStatusUpdated(poolId, pool.isActive, active);
        pool.isActive = active;
    }

    /**
     * @notice Updates the `allowCorePoolFallback` flag for a specific pool (excluding the Core Pool).
     * @param poolId ID of the pool to update.
     * @param allowFallback True to allow fallback to Core Pool, false to disable.
     * @custom:error InvalidOperationForCorePool Reverts when attempting to call pool-specific methods on the Core Pool.
     * @custom:error PoolDoesNotExist Reverts if the target pool ID does not exist.
     * @custom:event PoolFallbackStatusUpdated Emitted after the pool fallback flag is updated.
     */
    function setAllowCorePoolFallback(uint96 poolId, bool allowFallback) external {
        ensureAllowed("setAllowCorePoolFallback(uint96,bool)");

        if (poolId > lastPoolId) revert PoolDoesNotExist(poolId);
        if (poolId == corePoolId) revert InvalidOperationForCorePool();

        PoolData storage pool = pools[poolId];

        if (pool.allowCorePoolFallback == allowFallback) {
            return;
        }

        emit PoolFallbackStatusUpdated(poolId, pool.allowCorePoolFallback, allowFallback);
        pool.allowCorePoolFallback = allowFallback;
    }

    /**
     * @notice Updates the `isBorrowAllowed` flag for a market in a pool.
     * @param poolId The ID of the pool.
     * @param vToken The address of the market (vToken).
     * @param borrowAllowed The new borrow allowed status.
     * @custom:error PoolDoesNotExist Reverts if the pool ID is invalid.
     * @custom:error MarketConfigNotFound Reverts if the market is not listed in the pool.
     * @custom:event BorrowAllowedUpdated Emitted after the borrow permission for a market is updated.
     */
    function setIsBorrowAllowed(uint96 poolId, address vToken, bool borrowAllowed) external {
        ensureAllowed("setIsBorrowAllowed(uint96,address,bool)");

        if (poolId > lastPoolId) revert PoolDoesNotExist(poolId);

        PoolMarketId index = getPoolMarketIndex(poolId, vToken);
        Market storage m = _poolMarkets[index];

        if (!m.isListed) {
            revert MarketConfigNotFound();
        }

        if (m.isBorrowAllowed == borrowAllowed) {
            return;
        }

        emit BorrowAllowedUpdated(poolId, vToken, m.isBorrowAllowed, borrowAllowed);
        m.isBorrowAllowed = borrowAllowed;
    }

    /**
     * @dev Updates the valid price oracle. Used by _setPriceOracle and setPriceOracle
     * @param newOracle The new price oracle to be set
     * @return uint256 0=success, otherwise reverted
     */
    function __setPriceOracle(
        ResilientOracleInterface newOracle
    ) internal compareAddress(address(oracle), address(newOracle)) returns (uint256) {
        // Check caller is admin
        ensureAdmin();
        ensureNonzeroAddress(address(newOracle));

        // Track the old oracle for the comptroller
        ResilientOracleInterface oldOracle = oracle;

        // Set comptroller's oracle to newOracle
        oracle = newOracle;

        // Emit NewPriceOracle(oldOracle, newOracle)
        emit NewPriceOracle(oldOracle, newOracle);

        return uint256(Error.NO_ERROR);
    }

    /**
     * @dev Updates the close factor. Used by _setCloseFactor and setCloseFactor
     * @param newCloseFactorMantissa The new close factor to be set
     * @return uint256 0=success, otherwise reverted
     */
    function __setCloseFactor(
        uint256 newCloseFactorMantissa
    ) internal compareValue(closeFactorMantissa, newCloseFactorMantissa) returns (uint256) {
        // Check caller is admin
        ensureAdmin();

        Exp memory newCloseFactorExp = Exp({ mantissa: newCloseFactorMantissa });

        //-- Check close factor <= 0.9
        Exp memory highLimit = Exp({ mantissa: closeFactorMaxMantissa });
        //-- Check close factor >= 0.05
        Exp memory lowLimit = Exp({ mantissa: closeFactorMinMantissa });

        if (lessThanExp(highLimit, newCloseFactorExp) || greaterThanExp(lowLimit, newCloseFactorExp)) {
            return fail(Error.INVALID_CLOSE_FACTOR, FailureInfo.SET_CLOSE_FACTOR_VALIDATION);
        }

        uint256 oldCloseFactorMantissa = closeFactorMantissa;
        closeFactorMantissa = newCloseFactorMantissa;
        emit NewCloseFactor(oldCloseFactorMantissa, newCloseFactorMantissa);

        return uint256(Error.NO_ERROR);
    }

    /**
     * @dev Updates the collateral factor and the liquidation threshold. Used by setCollateralFactor
     * @param poolId The ID of the pool.
     * @param vToken The market to set the factor on
     * @param newCollateralFactorMantissa The new collateral factor to be set
     * @param newLiquidationThresholdMantissa The new liquidation threshold to be set
     * @return uint256 0=success, otherwise reverted
     */
    function __setCollateralFactor(
        uint96 poolId,
        VToken vToken,
        uint256 newCollateralFactorMantissa,
        uint256 newLiquidationThresholdMantissa
    ) internal returns (uint256) {
        ensureNonzeroAddress(address(vToken));

        // Check if pool exists
        if (poolId > lastPoolId) revert PoolDoesNotExist(poolId);

        // Verify market is listed in the pool
        Market storage market = _poolMarkets[getPoolMarketIndex(poolId, address(vToken))];
        ensureListed(market);

        //-- Check collateral factor <= 1
        if (newCollateralFactorMantissa > mantissaOne) {
            return fail(Error.INVALID_COLLATERAL_FACTOR, FailureInfo.SET_COLLATERAL_FACTOR_VALIDATION);
        }

        // If collateral factor != 0, fail if price == 0
        if (newCollateralFactorMantissa != 0 && oracle.getUnderlyingPrice(address(vToken)) == 0) {
            return fail(Error.PRICE_ERROR, FailureInfo.SET_COLLATERAL_FACTOR_WITHOUT_PRICE);
        }

        // Ensure that liquidation threshold <= 1
        if (newLiquidationThresholdMantissa > mantissaOne) {
            return fail(Error.INVALID_LIQUIDATION_THRESHOLD, FailureInfo.SET_LIQUIDATION_THRESHOLD_VALIDATION);
        }

        // Ensure that liquidation threshold >= CF
        if (newLiquidationThresholdMantissa < newCollateralFactorMantissa) {
            return
                fail(
                    Error.INVALID_LIQUIDATION_THRESHOLD,
                    FailureInfo.COLLATERAL_FACTOR_GREATER_THAN_LIQUIDATION_THRESHOLD
                );
        }

        // Set market's collateral factor to new collateral factor, remember old value
        uint256 oldCollateralFactorMantissa = market.collateralFactorMantissa;
        if (newCollateralFactorMantissa != oldCollateralFactorMantissa) {
            market.collateralFactorMantissa = newCollateralFactorMantissa;

            // Emit event with poolId, asset, old collateral factor, and new collateral factor
            emit NewCollateralFactor(poolId, vToken, oldCollateralFactorMantissa, newCollateralFactorMantissa);
        }

        uint256 oldLiquidationThresholdMantissa = market.liquidationThresholdMantissa;
        if (newLiquidationThresholdMantissa != oldLiquidationThresholdMantissa) {
            market.liquidationThresholdMantissa = newLiquidationThresholdMantissa;

            emit NewLiquidationThreshold(
                poolId,
                vToken,
                oldLiquidationThresholdMantissa,
                newLiquidationThresholdMantissa
            );
        }

        return uint256(Error.NO_ERROR);
    }

    /**
     * @dev Updates the liquidation incentive. Used by setLiquidationIncentive
     * @param poolId The ID of the pool.
     * @param vToken The market to set the Incentive for
     * @param newLiquidationIncentiveMantissa The new liquidation incentive to be set
     * @return uint256 0=success, otherwise reverted
     */
    function __setLiquidationIncentive(
        uint96 poolId,
        address vToken,
        uint256 newLiquidationIncentiveMantissa
    )
        internal
        compareValue(
            _poolMarkets[getPoolMarketIndex(poolId, vToken)].liquidationIncentiveMantissa,
            newLiquidationIncentiveMantissa
        )
        returns (uint256)
    {
        // Check if pool exists
        if (poolId > lastPoolId) revert PoolDoesNotExist(poolId);

        // Verify market is listed in the pool
        Market storage market = _poolMarkets[getPoolMarketIndex(poolId, vToken)];
        ensureListed(market);

        require(newLiquidationIncentiveMantissa >= mantissaOne, "incentive < 1e18");

        emit NewLiquidationIncentive(
            poolId,
            vToken,
            market.liquidationIncentiveMantissa,
            newLiquidationIncentiveMantissa
        );

        // Set liquidation incentive to new incentive
        market.liquidationIncentiveMantissa = newLiquidationIncentiveMantissa;

        return uint256(Error.NO_ERROR);
    }

    /**
     * @dev Updates the borrow caps. Used by _setMarketBorrowCaps and setMarketBorrowCaps
     * @param vTokens The markets to set the borrow caps on
     * @param newBorrowCaps The new borrow caps to be set
     */
    function __setMarketBorrowCaps(VToken[] memory vTokens, uint256[] memory newBorrowCaps) internal {
        ensureAllowed("_setMarketBorrowCaps(address[],uint256[])");

        uint256 numMarkets = vTokens.length;
        uint256 numBorrowCaps = newBorrowCaps.length;

        require(numMarkets != 0 && numMarkets == numBorrowCaps, "invalid input");

        for (uint256 i; i < numMarkets; ++i) {
            borrowCaps[address(vTokens[i])] = newBorrowCaps[i];
            emit NewBorrowCap(vTokens[i], newBorrowCaps[i]);
        }
    }

    /**
     * @dev Updates the supply caps. Used by _setMarketSupplyCaps and setMarketSupplyCaps
     * @param vTokens The markets to set the supply caps on
     * @param newSupplyCaps The new supply caps to be set
     */
    function __setMarketSupplyCaps(VToken[] memory vTokens, uint256[] memory newSupplyCaps) internal {
        ensureAllowed("_setMarketSupplyCaps(address[],uint256[])");

        uint256 numMarkets = vTokens.length;
        uint256 numSupplyCaps = newSupplyCaps.length;

        require(numMarkets != 0 && numMarkets == numSupplyCaps, "invalid input");

        for (uint256 i; i < numMarkets; ++i) {
            supplyCaps[address(vTokens[i])] = newSupplyCaps[i];
            emit NewSupplyCap(vTokens[i], newSupplyCaps[i]);
        }
    }

    /**
     * @dev Updates the prime token. Used by _setPrimeToken and setPrimeToken
     * @param _prime The new prime token to be set
     * @return uint256 0=success, otherwise reverted
     */
    function __setPrimeToken(IPrime _prime) internal returns (uint) {
        ensureAdmin();
        ensureNonzeroAddress(address(_prime));

        IPrime oldPrime = prime;
        prime = _prime;
        emit NewPrimeToken(oldPrime, _prime);

        return uint(Error.NO_ERROR);
    }

    /**
     * @dev Updates the forced liquidation. Used by _setForcedLiquidation and setForcedLiquidation
     * @param vTokenBorrowed The market to set the forced liquidation on
     * @param enable Whether to enable forced liquidations
     */
    function __setForcedLiquidation(address vTokenBorrowed, bool enable) internal {
        ensureAllowed("_setForcedLiquidation(address,bool)");
        if (vTokenBorrowed != address(vaiController)) {
            ensureListed(getCorePoolMarket(vTokenBorrowed));
        }
        isForcedLiquidationEnabled[vTokenBorrowed] = enable;
        emit IsForcedLiquidationEnabledUpdated(vTokenBorrowed, enable);
    }

    /**
     * @dev Updates the actions paused. Used by _setActionsPaused and setActionsPaused
     * @param markets_ The markets to set the actions paused on
     * @param actions_ The actions to set the paused state on
     * @param paused_ The new paused state to be set
     */
    function __setActionsPaused(address[] memory markets_, Action[] memory actions_, bool paused_) internal {
        ensureAllowed("_setActionsPaused(address[],uint8[],bool)");

        uint256 numMarkets = markets_.length;
        uint256 numActions = actions_.length;
        for (uint256 marketIdx; marketIdx < numMarkets; ++marketIdx) {
            for (uint256 actionIdx; actionIdx < numActions; ++actionIdx) {
                setActionPausedInternal(markets_[marketIdx], actions_[actionIdx], paused_);
            }
        }
    }
}

